import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const navigate = useNavigate();

  // Form State for Adding Users/Stores
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    address: "",
    role: "Normal User",
  });

const loadData = async () => {
  try {
    // Make all API calls in parallel
    const [uRes, sRes, statsRes] = await Promise.all([
      api.get("/users"), 
      api.get("/stores"), 
      api.get("/users/stats") 
    ]);

    console.log("Raw Users API Response:", uRes.data);
    
    // Try different ways to extract the users array:
    let userData = [];
    if (uRes.data && Array.isArray(uRes.data)) {
      // If backend returns array directly
      userData = uRes.data;
    } else if (uRes.data && uRes.data.rows && Array.isArray(uRes.data.rows)) {
      // If backend returns { rows: [...] }
      userData = uRes.data.rows;
    } else if (uRes.data && uRes.data.users && Array.isArray(uRes.data.users)) {
      // If backend returns { users: [...] }
      userData = uRes.data.users;
    } else if (uRes.data && uRes.data.data && Array.isArray(uRes.data.data)) {
      // If backend returns { data: [...] }
      userData = uRes.data.data;
    }
    console.log("Extracted Users:", userData);

    let storeData = [];
    if (sRes.data && Array.isArray(sRes.data)) {
      storeData = sRes.data;
    } else if (sRes.data && sRes.data.rows) {
      storeData = sRes.data.rows;
    } else if (sRes.data && sRes.data.stores) {
      storeData = sRes.data.stores;
    } else if (sRes.data && sRes.data.data) {
      storeData = sRes.data.data;
    }
    console.log("Extracted Stores:", storeData);

    setUsers(userData);
    setStores(storeData);
    setStats(statsRes.data || { totalUsers: 0, totalStores: 0, totalRatings: 0 });
    
  } catch (err) {
    console.error("Failed to load data:", err.response?.data || err.message);
    alert("Failed to load data. Check console for details.");
  }
};
  useEffect(() => {
    loadData();
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login'); // Requirement: Can log out from system [cite: 35, 54, 61]
  };

  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      // Requirement: Admin can add new stores, normal users, and admins 
      if (activeTab === "users") {
        await api.post("/auth/register", formData);
      } else {
        await api.post("/stores", { 
          name: formData.name, 
          email: formData.email, 
          address: formData.address 
        });
      }
      setShowModal(false);
      setFormData({ name: "", email: "", password: "", address: "", role: "Normal User" });
      loadData();
      alert("Added successfully!");
    } catch (err) {
      alert(err.response?.data?.message || "Operation failed. Check validations.");
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm("Permanently delete this user?")) {
      try {
        await api.delete(`/users/${userId}`);
        loadData();
      } catch (err) {
        alert("Delete failed");
      }
    }
  };

  // Requirement: Apply filters based on Name, Email, Address, and Role 
  const filteredData = useMemo(() => {
    const data = activeTab === "users" ? users : stores;
    const term = search.toLowerCase();
    return data.filter(item => 
      item.name?.toLowerCase().includes(term) ||
      item.email?.toLowerCase().includes(term) ||
      item.address?.toLowerCase().includes(term) ||
      (activeTab === "users" && item.role?.toLowerCase().includes(term))
    );
  }, [users, stores, activeTab, search]);

  const paginatedData = filteredData.slice(
    (currentPage - 1) * itemsPerPage, 
    currentPage * itemsPerPage
  );

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      
      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => { setActiveTab("users"); setCurrentPage(1); }}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => { setActiveTab("stores"); setCurrentPage(1); }}>Stores</button>
        </div>
        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      <main className="admin-main">
        {/* Requirement: Dashboard Stats [cite: 18, 19, 20, 21] */}
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            <div className="header-actions">
              <input 
                className="admin-search" 
                placeholder="Filter by name, email, address..." 
                value={search} 
                onChange={(e) => setSearch(e.target.value)} 
              />
              <button className="action-btn" onClick={() => setShowModal(true)}>
                + Add {activeTab === "users" ? "User" : "Store"}
              </button>
            </div>
          </div>

          <ul className="admin-list">
            {paginatedData.length === 0 ? (
              <li className="empty-state">No records found.</li>
            ) : (
              paginatedData.map((item) => (
                <li key={activeTab === "users" ? item.user_id : item.store_id} className="admin-list-item">
                  <div className="item-main-info">
                    <span className="item-name">{item.name}</span>
                    <span className="item-sub">
                      {/* Requirement: Display Name, Email, Address, Role [cite: 30, 32] */}
                      {item.email} • {item.address} • <strong>{activeTab === "users" ? item.role : "Store"}</strong>
                    </span>
                    {/* Requirement: If Store Owner or Store, display Rating  */}
                    {(activeTab === "stores" || item.role === "Store Owner") && (
                      <div className="item-rating-info">
                        ★ {Number(item.average_rating || item.rating || 0).toFixed(1)}
                      </div>
                    )}
                  </div>
                  <div className="item-actions">
                    {activeTab === "users" && (
                      <button onClick={() => handleDeleteUser(item.user_id)} className="delete-btn">Delete</button>
                    )}
                  </div>
                </li>
              ))
            )}
          </ul>

          {filteredData.length > itemsPerPage && (
            <div className="admin-pagination">
              <button onClick={() => setCurrentPage(p => Math.max(1, p - 1))} disabled={currentPage === 1}>Prev</button>
              <span className="page-indicator">Page {currentPage}</span>
              <button onClick={() => setCurrentPage(p => p + 1)} disabled={paginatedData.length < itemsPerPage}>Next</button>
            </div>
          )}
        </section>
      </main>

      {/* Requirement: Admin Add New User/Store Modal  */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Add New {activeTab === "users" ? "User" : "Store"}</h3>
            <form onSubmit={handleAddSubmit} className="admin-form">
              <input 
                placeholder="Name (20-60 chars)" 
                required 
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})} 
              />
              <input 
                type="email" 
                placeholder="Email" 
                required 
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})} 
              />
              {activeTab === "users" && (
                <>
                  <input 
                    type="password" 
                    placeholder="Password (8-16, 1 Upper, 1 Special)" 
                    required 
                    onChange={(e) => setFormData({...formData, password: e.target.value})} 
                  />
                  <select value={formData.role} onChange={(e) => setFormData({...formData, role: e.target.value})}>
                    <option value="Normal User">Normal User</option>
                    <option value="Store Owner">Store Owner</option>
                    <option value="System Administrator">Admin</option>
                  </select>
                </>
              )}
              <textarea 
                placeholder="Address (Max 400 chars)" 
                rows="3"
                value={formData.address}
                onChange={(e) => setFormData({...formData, address: e.target.value})} 
              />
              <div className="modal-btns">
                <button type="submit" className="confirm-btn">Save</button>
                <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
