import React, { useEffect, useState, useMemo } from 'react';
import api from '../../services/api';

const ITEMS_PER_PAGE = 5;

const UserDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratingSubmitting, setRatingSubmitting] = useState(null);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);

  // Load stores
  const load = () => api.get('/stores').then(r => setStores(r.data)).catch(() => {});
  useEffect(() => { load(); }, []);

  // Rating submission
  const rate = async (storeId, rating) => {
    setRatingSubmitting(storeId);
    try {
      await api.post(`/ratings/${storeId}`, { rating });
      load();
    } finally {
      setRatingSubmitting(null);
    }
  };

  // Filtered and paginated stores
  const filteredStores = useMemo(() => (
    stores.filter(s => s.name.toLowerCase().includes(search.trim().toLowerCase()))
  ), [stores, search]);

  const totalPages = Math.ceil(filteredStores.length / ITEMS_PER_PAGE);
  const currentStores = filteredStores.slice(
    (page-1)*ITEMS_PER_PAGE, page*ITEMS_PER_PAGE
  );

  useEffect(() => {
    setPage(1); // Reset to first page on search
  }, [search]);

  // Handle pagination click
  const handlePage = (p) => {
    if (p >= 1 && p <= totalPages) setPage(p);
  };

  return (
    <>
    <style>{`
      body, html, #root {
        background: #f3f6fb;
        font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
        color: #1a1a1e;
      }

      .dashboard-container {
        max-width: 600px;
        margin: 56px auto;
        background: linear-gradient(120deg, #fff 80%, #f6f2ff 100%);
        border-radius: 16px;
        box-shadow: 0 12px 28px rgba(31, 35, 90, 0.10),0 1.5px 12px #a49ddb18;
        padding: 38px 28px 28px 28px;
        min-height: 690px;
        animation: fadePopIn 0.7s cubic-bezier(0.5,0.1,0.3,1);
        position: relative;
      }

      .dashboard-title {
        font-weight: 800;
        font-size: 2.12rem;
        text-align: center;
        color: #29264c;
        margin-bottom: 32px;
        letter-spacing: 1px;
        user-select: none;
      }

      .search-box {
        width: 100%;
        margin-bottom: 22px;
        padding: 13px 15px;
        border-radius: 8px;
        border: 1.5px solid #dedbf7;
        font-size: 1.05rem;
        background: #f8f7fc;
        transition: border-color 0.2s;
        outline: none;
        margin-top: -6px;
        box-sizing: border-box;
      }
      .search-box:focus {
        border-color: #8f77ea;
        background: #fff;
      }

      .store-list {
        list-style-type: none;
        padding: 0;
        margin: 0 0 36px 0;
      }

      .store-card {
        background: #fff;
        border-radius: 13px;
        box-shadow: 0 1px 9px #8d6adf13, 0 1.5px 7px #ca9cf618;
        margin-bottom: 18px;
        padding: 18px 24px 16px 24px;
        display: flex;
        justify-content: space-between;
        align-items: center;
        border: 1.5px solid #ece9fc;
        transition: box-shadow 0.22s, border 0.2s, transform 0.13s;
      }
      .store-card:hover {
        box-shadow: 0 6px 28px #7f56da20, 0 4px 18px #464bda0a;
        border: 1.5px solid #b3abed;
        transform: translateY(-2px) scale(1.012);
      }

      .store-info {
        display: flex;
        flex-direction: column;
        gap: 4px;
      }
      .store-name {
        color: #6b38d7;
        font-weight: 700;
        font-size: 1.24rem;
        margin-bottom: 1px;
        letter-spacing: 0.25px;
      }
      .rating-info {
        color: #645599;
        font-size: 1.01rem;
        margin-left: 2px;
      }

      .rate-buttons {
        display: flex;
        gap: 6px;
        margin-left: 16px;
      }
      .rate-btn {
        background: #eae6fa;
        border: none;
        color: #7f56da;
        font-weight: 800;
        font-size: 0.99rem;
        border-radius: 7px;
        padding: 8px 11px;
        cursor: pointer;
        transition: background 0.18s, color 0.17s, transform 0.13s;
        box-shadow: 0 0.5px 1px #c2beff26;
      }
      .rate-btn:hover:not(:disabled),.rate-btn:focus:not(:disabled){
        background: #7f56da;
        color: #fff;
        transform: translateY(-1.5px) scale(1.07);
        outline: none;
      }
      .rate-btn:disabled {
        background: #f4f6fc;
        color: #b0a3da;
        cursor: not-allowed;
        transform: none;
        box-shadow: none;
      }

      .pagination {
        width: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
        gap: 6px;
        margin-top: 6px;
        margin-bottom: 2px;
        flex-wrap: wrap;
      }
      .page-btn {
        border: none;
        background: #e9e6ed;
        color: #745aec;
        font-weight: 700;
        padding: 7px 13px;
        border-radius: 8px;
        font-size: 1rem;
        min-width: 36px;
        cursor: pointer;
        transition: background 0.14s, color 0.18s, box-shadow 0.15s;
      }
      .page-btn.active {
  background-color: #7f56da;   /* main purple */
  color: #7f56da;
  box-shadow: 0 4px 12px rgba(127, 86, 218, 0.4);  /* glowing purple */
  border: 1.5px solid #5b32c7;  /* darker purple border */
  transform: scale(1.08);  /* stand-out effect */
}

      .page-btn:disabled {
        color: #c9c3e3;
        background: #f0ecfa;
        cursor: not-allowed;
      }

      @keyframes fadePopIn {
        0% { opacity: 0; transform: translateY(30px) scale(0.98);}
        100% { opacity: 1; transform: translateY(0) scale(1);}
      }

      @media (max-width: 700px) {
        .dashboard-container {
          min-height: 0;
          width: 97vw;
          margin: 31px 2vw;
          padding: 24px 6.5vw 18px 6vw;
        }
        .store-card {padding: 13px 10px;}
        .rate-btn {padding:6px 8px;}
        .dashboard-title {font-size: 1.46rem;}
      }
    `}</style>
    <div className='dashboard-container'>
      <div className='dashboard-title'>Your Dashboard</div>
      <input
        className="search-box"
        placeholder="Search store by name..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        aria-label="Search stores"
      />
      <ul className="store-list">
        {currentStores.length === 0 && (
          <li style={{
            border: 'none',
            background: 'none',
            textAlign: 'center',
            color: '#9c94bc',
            fontSize: '1.13rem',
            marginTop: '48px'
          }}>
            No stores found.
          </li>
        )}
        {currentStores.map(s => (
          <li className="store-card" key={s.store_id}>
            <div className="store-info">
              <span className="store-name">{s.name}</span>
              <span className="rating-info">
                avg <b>{Number(s.average_rating).toFixed(1)}</b> ({s.rating_count})
              </span>
            </div>
            <div className="rate-buttons">
              {[1,2,3,4,5].map(n => (
                <button
                  key={n}
                  className="rate-btn"
                  disabled={ratingSubmitting === s.store_id}
                  onClick={() => rate(s.store_id, n)}
                  aria-label={`Rate ${s.name} with ${n} star${n > 1 ? 's' : ''}`}
                >{n}</button>
              ))}
            </div>
          </li>
        ))}
      </ul>
      {totalPages > 1 && (
        <div className="pagination">
          <button className="page-btn" onClick={()=>handlePage(page-1)} disabled={page===1}>Prev</button>
          {Array.from({length: totalPages}, (_,i)=><button
            key={i+1}
            className={"page-btn"+(page===i+1?' active':'')}
            onClick={()=>handlePage(i+1)}
            disabled={page===i+1}
          >{i+1}</button>)}
          <button className="page-btn" onClick={()=>handlePage(page+1)} disabled={page===totalPages}>Next</button>
        </div>
      )}
    </div>
    </>
  );
};

export default UserDashboard;
