import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5; // Requirement fixed to 5 rows per page
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    address: "",
    role: "Normal User",
  });

  const loadData = async () => {
    try {
      const [uRes, sRes, statsRes] = await Promise.all([
        api.get("/users"),
        api.get("/stores"),
        api.get("/users/stats")
      ]);

      const extract = (res) => (Array.isArray(res.data) ? res.data : (res.data.rows || res.data.users || res.data.stores || []));
      
      setUsers(extract(uRes));
      setStores(extract(sRes));
      setStats(statsRes.data || { totalUsers: 0, totalStores: 0, totalRatings: 0 });
    } catch (err) {
      console.error("Failed to load data:", err);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Reset page when tab changes
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setCurrentPage(1);
  };

  const filteredData = useMemo(() => {
    const data = activeTab === "users" ? users : stores;
    const term = search.toLowerCase();
    return data.filter(item => 
      item.name?.toLowerCase().includes(term) ||
      item.email?.toLowerCase().includes(term) ||
      item.address?.toLowerCase().includes(term) ||
      (activeTab === "users" && item.role?.toLowerCase().includes(term))
    );
  }, [users, stores, activeTab, search]);

  // Pagination Logic
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div className="admin-wrapper">
      {/* Fixed Sidebar */}
      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button 
            className={activeTab === "users" ? "active" : ""} 
            onClick={() => handleTabChange("users")}
          >
            Users
          </button>
          <button 
            className={activeTab === "stores" ? "active" : ""} 
            onClick={() => handleTabChange("stores")}
          >
            Stores
          </button>
        </div>
        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      {/* Scrollable Main Content */}
      <main className="admin-main">
        <div className="dash-glow dash-glow-1" />
        
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            <div className="header-actions">
              <input 
                className="admin-search" 
                placeholder="Filter records..." 
                value={search} 
                onChange={(e) => { setSearch(e.target.value); setCurrentPage(1); }} 
              />
              <button className="action-btn" onClick={() => setShowModal(true)}>
                + Add {activeTab === "users" ? "User" : "Store"}
              </button>
            </div>
          </div>

          <div className="table-container">
            <table className="admin-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Address</th>
                  <th>{activeTab === "users" ? "Role" : "Status"}</th>
                  <th className="text-center">Rating</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {paginatedData.length === 0 ? (
                  <tr><td colSpan="6" className="empty-state">No records found.</td></tr>
                ) : (
                  paginatedData.map((item) => (
                    <tr key={activeTab === "users" ? item.user_id : item.store_id}>
                      <td className="font-bold">{item.name}</td>
                      <td>{item.email}</td>
                      <td className="truncate-cell">{item.address || "—"}</td>
                      <td>
                        <span className={`badge ${item.role?.replace(/\s+/g, '-').toLowerCase() || 'store-badge'}`}>
                          {activeTab === "users" ? item.role : "Registered Store"}
                        </span>
                      </td>
                      <td className="text-center">
                        {(activeTab === "stores" || item.role === "Store Owner") ? (
                          <span className="rating-pill">★ {Number(item.average_rating || item.rating || 0).toFixed(1)}</span>
                        ) : (
                          <span className="dash-placeholder">—</span>
                        )}
                      </td>
                      <td className="text-center">
                        {activeTab === "users" && (
                          <button className="icon-delete-btn" title="Delete User">
                            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                              <polyline points="3 6 5 6 21 6"></polyline>
                              <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path>
                            </svg>
                          </button>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Pagination Controls */}
          {filteredData.length > 0 && (
            <div className="admin-pagination">
              <button 
                onClick={() => setCurrentPage(p => Math.max(1, p - 1))} 
                disabled={currentPage === 1}
              >
                Prev
              </button>
              <span className="page-indicator">Page {currentPage} of {totalPages || 1}</span>
              <button 
                onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))} 
                disabled={currentPage === totalPages || totalPages === 0}
              >
                Next
              </button>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default AdminDashboard;
