import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './StoreOwner.css';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState('');
  const navigate = useNavigate();

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const userId = user.user_id; // This should be 6 for mohit78601@gmail.com

  console.log('üîç Current User:', user);
  console.log('üîç User ID from localStorage:', userId);
  console.log('üîç User Name:', user.name);
  console.log('üîç User Role:', user.role);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // REMOVE THIS DUPLICATE LOAD FUNCTION FROM THE TOP AND KEEP THIS ONE:
  const load = async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      const allStores = response.data || [];
      setStores(allStores);
      
      // Enhanced Debug: Show ALL store data including owner_id
      console.log('üìä All stores loaded:', allStores.length);
      console.log('üîç Full API Response Structure:', allStores[0] ? Object.keys(allStores[0]) : 'No stores');
      
      allStores.forEach((store, index) => {
        console.log(`Store ${index + 1}: "${store.name}" | Owner ID: ${store.owner_id} (Type: ${typeof store.owner_id}) | Your ID: ${userId}`);
      });
      
    } catch (e) {
      console.error("Failed to load stores", e);
      setAlert('Failed to load stores');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      await load();
      setActiveTab('stores');
      setCurrentPage(1);
      setAlert('Store created successfully!');
      setTimeout(() => setAlert(''), 3000);
    } catch (e) {
      // Better error logging
      console.error('Store creation error:', e.response?.data || e.message);
      setAlert('Failed to add store: ' + (e.response?.data?.message || e.message));
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  // FIXED: Filter stores owned by the current user
  const ownerStores = stores.filter((store) => {
    // Convert both to numbers for comparison
    const storeOwnerId = Number(store.owner_id);
    const currentUserId = Number(userId);
    
    console.log(`Comparing: Store Owner ID = ${storeOwnerId} (${typeof storeOwnerId}) | Your ID = ${currentUserId} (${typeof currentUserId})`);
    
    return storeOwnerId === currentUserId;
  });
  
  console.log('‚úÖ Your stores count:', ownerStores.length);
  console.log('üìã Your stores:', ownerStores);
  
  const totalPages = Math.ceil(ownerStores.length / itemsPerPage);
  
  const paginatedStores = ownerStores.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="owner-dash-wrapper">
      {/* Background Effects */}
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

 <nav className="owner-sidebar">
  <div className="owner-sidebar-top">
    <div className="owner-logo">StoreRate<span>.</span></div>
    
    {/* Wrap nav items to control spacing */}
    <div className="owner-nav-content">
      <div className="nav-group">
        <button
          className={activeTab === 'create' ? 'active' : ''}
          onClick={() => setActiveTab('create')}
        >
          Create Store
        </button>
        <button
          className={activeTab === 'stores' ? 'active' : ''}
          onClick={() => setActiveTab('stores')}
        >
          Your Stores
        </button>
      </div>

      <button onClick={handleLogout} className="owner-logout-btn">
        Logout
      </button>
    </div>
  </div>

  {/* User Info - now pushed up by the flex container */}
  <div className="sidebar-user-info">
    <div className="user-avatar">
      {user.name?.charAt(0)?.toUpperCase() || 'U'}
    </div>
    <div className="user-details">
      <strong>{user.name || 'Store Owner'}</strong>
      <span>Store Owner</span>
      <small>ID: {userId}</small>
    </div>
  </div>
</nav>

      {/* Main Content */}
      <main className="owner-content">
        {activeTab === 'create' && (
          <div className="owner-glass-card fade-in">
            <h2>Register New Store</h2>
            <p className="card-subtitle">Enter details to add your store to the platform</p>
            
            {/* User Info Display */}
            <div className="user-info-badge">
              Store will be registered under: <strong>{user.name}</strong> (ID: {userId})
            </div>
            
            <form onSubmit={submit} className="owner-form">
              <input
                type="text"
                placeholder="Store Name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                required
              />
              <input
                type="email"
                placeholder="Contact Email"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                required
              />
              <textarea
                placeholder="Address"
                value={form.address}
                onChange={(e) => setForm({ ...form, address: e.target.value })}
                rows="3"
                className="owner-form-input"
              />
              <button type="submit" className="owner-btn" disabled={loading}>
                {loading ? 'Creating...' : 'Create Store'}
              </button>
            </form>
          </div>
        )}

        {activeTab === 'stores' && (
          <div className="owner-glass-card fade-in">
            <div className="content-header">
              <div>
                <h2>Your Stores Portfolio</h2>
                <p className="card-subtitle">
                  Showing stores for: <strong>{user.name}</strong>
                  <span className="store-count"> ({ownerStores.length} stores)</span>
                </p>
              </div>
              <div className="user-id-badge">
                Your User ID: <strong>{userId}</strong>
              </div>
            </div>
            
            <div className="table-container">
            <table className="stores-table">
    <thead>
      <tr>
        <th>Store Name</th>
        <th>Email</th>
        <th>Address</th>
        {/* REMOVED OWNER ID TH */}
        <th>Rating</th>
        <th className="text-center">Total Ratings</th>
        <th>Status</th>
      </tr>
    </thead>
    <tbody>
      {paginatedStores.map((store) => (
        <tr key={store.store_id}>
          <td className="font-bold">{store.name}</td>
          <td>{store.email}</td>
          <td className="truncate-cell" title={store.address}>
            {store.address || '‚Äî'}
          </td>
          {/* REMOVED OWNER ID TD */}
          <td className="text-center">
            <span className="rating-pill">
              ‚òÖ {Number(store.average_rating || 0).toFixed(1)}
            </span>
          </td>
          <td className="text-center">
            {/* Class logic handled in CSS for white text/no background */}
            <span className="rating-count-clean">
              {store.rating_count || 0}
            </span>
          </td>
          <td>
            <span className="status-badge">Active</span>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
              
              {ownerStores.length === 0 && (
                <div className="empty-state-card">
                  <div className="empty-icon">üè™</div>
                  <h3>No Stores Found</h3>
                  <p>
                    You don't have any stores yet. <br />
                  </p>
                  

                  
                  <button 
                    className="empty-action-btn"
                    onClick={() => setActiveTab('create')}
                  >
                    Create Your First Store
                  </button>
                </div>
              )}
            </div>

            {/* Pagination */}
            {ownerStores.length > itemsPerPage && (
              <div className="owner-pagination">
                <button 
                  onClick={() => goToPage(currentPage - 1)} 
                  disabled={currentPage === 1}
                >
                  Prev
                </button>
                
                <span className="page-indicator">
                  Page {currentPage} of {totalPages}
                </span>
                
                <button 
                  onClick={() => goToPage(currentPage + 1)} 
                  disabled={currentPage === totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </div>
        )}
      </main>

      {alert && <div className="owner-alert-toast">{alert}</div>}
    </div>
  );
};

export default OwnerDashboard;
