import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [activeTab, setActiveTab] = useState("users");
  const [currentPage, setCurrentPage] = useState(1);
  const [storeSearch, setStoreSearch] = useState("");

  const itemsPerPage = 5;
  const maxPageButtons = 5;

  useEffect(() => {
    api.get("/users").then((r) => setUsers(r.data)).catch(() => {});
    api.get("/stores").then((r) => setStores(r.data)).catch(() => {});
  }, []);

  // Filtered stores for search
  const filteredStores = useMemo(() => {
    return stores.filter((s) =>
      s.name.toLowerCase().includes(storeSearch.trim().toLowerCase())
    );
  }, [stores, storeSearch]);

  // Data based on active tab (users or filtered stores)
  const data = activeTab === "users" ? users : filteredStores;
  const totalPages = Math.ceil(data.length / itemsPerPage);

  // Paginated current page data
  const paginatedData = data.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const getPageNumbers = () => {
    const pages = [];
    let start = Math.max(1, currentPage - Math.floor(maxPageButtons / 2));
    let end = start + maxPageButtons - 1;
    if (end > totalPages) {
      end = totalPages;
      start = Math.max(1, end - maxPageButtons + 1);
    }
    for (let i = start; i <= end; i++) pages.push(i);
    return pages;
  };

  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  // Reset page when changing tabs or storeSearch
  React.useEffect(() => {
    setCurrentPage(1);
  }, [activeTab, storeSearch]);

  return (
    <>
      <style>{`
        body, html, #root {
          margin: 0;
          height: 100%;
          font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
            Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          background-color: #f4f7fc;
          color: #222;
        }

        .dashboard-wrapper {
          display: flex;
          height: 100vh;
          overflow: hidden;
          background: #fff;
          flex-direction: row;
        }

        nav.sidebar {
          width: 260px;
          background: linear-gradient(135deg, #7f56da, #7563fb 85%);
          color: #fff;
          display: flex;
          flex-direction: column;
          padding: 60px 0;
          font-weight: 700; /* Increased font weight */
          font-size: 1.0rem; /* Increased font size */
          box-shadow: 2px 0 22px 0 #ede9fa;
          position: fixed;
          top: 0;
          left: 0;
          bottom: 0;
          z-index: 1000;
        }
        nav.sidebar button {
          background: transparent;
          border: none;
          color: #fff;
          padding: 14px 36px;
          width: 100%;
          text-align: left;
          cursor: pointer;
          transition: background 0.18s, color 0.22s;
          margin-bottom: 6px;
          outline: none;
          font-weight: 700; /* Increased font weight */
          font-size: 1.3rem; /* Increased font size */
        }
        nav.sidebar button.active {
          background: #fff;
          color: #7f56da;
          font-weight: 900; /* Make active even bolder */
          border-radius: 0 12px 12px 0;
          box-shadow: 0 2px 12px rgba(127,86,218,0.15);
        }
        nav.sidebar button:not(.active):hover {
          background: rgba(255,255,255,0.15);
        }

        main.content {
          flex-grow: 1;
          background: #fafbff;
          overflow-y: auto;
          padding: 48px 36px;
          margin-left: 260px;
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: flex-start;
        }

        .center-wrapper {
          width: 100%;
          max-width: 700px;
          background: #fff;
          border-radius: 18px;
          box-shadow: 0 12px 40px rgba(127, 86, 218, 0.12);
          padding: 48px 36px;
          animation: fadeSlideIn 0.8s ease;
        }

        .center-wrapper h1 {
          font-weight: 800;
          font-size: 2rem;
          color: #222a35;
          margin-top: 0;
          margin-bottom: 28px;
          text-align: center;
          user-select: none;
        }

        .search-box {
          width: 100%;
          padding: 12px 16px;
          margin-bottom: 24px;
          font-size: 1rem;
          border-radius: 10px;
          border: 1.5px solid #dedbf7;
          outline: none;
          background: #f8f7fc;
          transition: border-color 0.25s ease;
        }
        .search-box:focus {
          border-color: #7f56da;
          background: #fff;
          box-shadow: 0 0 10px #b99aff33;
        }

        ul.list {
          list-style: none;
          padding: 0;
          margin: 0;
          display: flex;
          flex-direction: column;
          gap: 14px;
          user-select: none;
        }

        ul.list li {
          background: #f4f7fc;
          border-radius: 11px;
          box-shadow: 0 2px 9px #7f56da10;
          padding: 18px 24px;
          font-weight: 500;
          font-size: 1.1rem;
          color: #7f56da;
          display: flex;
          justify-content: space-between;
          align-items: center;
          transition: box-shadow 0.25s ease;
        }
        ul.list li:hover {
          box-shadow: 0 10px 26px #7f56da1a;
        }

        .pagination {
          display: flex;
          justify-content: center;
          margin-top: 24px;
          gap: 8px;
          flex-wrap: wrap;
          user-select: none;
          align-items: center;
        }
        .pagination button {
          border: none;
          padding: 7px 14px;
          border-radius: 6px;
          background: #ddd;
          color: #333;
          font-weight: 500;
          cursor: pointer;
          transition: background 0.2s;
        }
        .pagination button.page-number.current {
          background: #7f56da;
          color: #fff;
          font-weight: 700;
        }
        .pagination button:disabled {
          background: #eee;
          color: #aaa;
          cursor: not-allowed;
        }

        @keyframes fadeSlideIn {
          0% { opacity: 0; transform: translateY(18px); }
          100% { opacity: 1; transform: translateY(0); }
        }

        @media (max-width: 800px) {
          main.content {
            margin-left: 0;
            padding: 24px 16px;
          }
          nav.sidebar {
            flex-direction: row;
            width: 100%;
            height: auto;
            padding: 12px 0;
            box-shadow: none;
          }
          nav.sidebar button {
            text-align: center;
            flex-grow: 1;
            padding: 12px 0;
            font-size: 1rem;
          }
        }
      `}</style>

      <div className="dashboard-wrapper">
        <nav className="sidebar" aria-label="Admin dashboard navigation">
          <button
            type="button"
            className={activeTab === "users" ? "active" : ""}
            onClick={() => {
              setActiveTab("users");
              setCurrentPage(1);
              setStoreSearch("");
            }}
            aria-current={activeTab === "users" ? "page" : undefined}
          >
            Users
          </button>
          <button
            type="button"
            className={activeTab === "stores" ? "active" : ""}
            onClick={() => {
              setActiveTab("stores");
              setCurrentPage(1);
            }}
            aria-current={activeTab === "stores" ? "page" : undefined}
          >
            Stores
          </button>
        </nav>

        <main className="content">
          <div className="center-wrapper" role="region" aria-live="polite">
            <h1>{activeTab === "users" ? "All Users" : "All Stores"}</h1>

            {activeTab === "stores" && (
              <input
                type="text"
                className="search-box"
                placeholder="Search stores by name..."
                value={storeSearch}
                onChange={(e) => setStoreSearch(e.target.value)}
                aria-label="Search stores"
              />
            )}

            <ul className="list" tabIndex={0} aria-label={activeTab === "users" ? "Users list" : "Stores list"}>
              {activeTab === "users" &&
                paginatedData.map((u) => (
                  <li key={u.user_id}>
                    <span>{u.name}</span>
                    <span style={{ fontSize: "0.95rem", color: "#555" }}>
                      {u.email} ({u.role})
                    </span>
                  </li>
                ))}

              {activeTab === "stores" &&
                paginatedData.map((s) => (
                  <li key={s.store_id}>
                    <span>{s.name}</span>
                    <span style={{ fontSize: "0.95rem", color: "#555" }}>
                      rating {Number(s.average_rating).toFixed(1)} ({s.rating_count})
                    </span>
                  </li>
                ))}

              {data.length === 0 && (
                <li style={{ textAlign: "center", color: "#777" }}>
                  No {activeTab} found.
                </li>
              )}
            </ul>

            {data.length > itemsPerPage && (
              <div
                className="pagination"
                role="navigation"
                aria-label="Pagination Navigation"
              >
                <button
                  onClick={() => goToPage(currentPage - 1)}
                  disabled={currentPage === 1}
                  aria-label="Previous page"
                >
                  Prev
                </button>
                {getPageNumbers().map((p) => (
                  <button
                    key={p}
                    className={`page-number ${p === currentPage ? "current" : ""}`}
                    onClick={() => goToPage(p)}
                    aria-current={p === currentPage ? "page" : undefined}
                  >
                    {p}
                  </button>
                ))}
                <button
                  onClick={() => goToPage(currentPage + 1)}
                  disabled={currentPage === totalPages}
                  aria-label="Next page"
                >
                  Next
                </button>
              </div>
            )}
          </div>
        </main>
      </div>
    </>
  );
};

export default AdminDashboard;
