import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [currentPage, setCurrentPage] = useState(1);
  const [storeSearch, setStoreSearch] = useState("");
  const navigate = useNavigate();

  const itemsPerPage = 5;

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    // Requirement: Dashboard displaying totals 
    api.get("/users").then((r) => setUsers(r.data)).catch(() => {});
    api.get("/stores").then((r) => setStores(r.data)).catch(() => {});
    api.get("/admin/stats").then((r) => setStats(r.data)).catch(() => {});
  }, []);

  // Requirement: Apply filters on listings based on Name [cite: 33, 45]
  const filteredData = useMemo(() => {
    if (activeTab === "users") {
      return users.filter(u => u.name.toLowerCase().includes(storeSearch.trim().toLowerCase()));
    }
    return stores.filter(s => s.name.toLowerCase().includes(storeSearch.trim().toLowerCase()));
  }, [users, stores, activeTab, storeSearch]);

  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const goToPage = (page) => { if (page >= 1 && page <= totalPages) setCurrentPage(page); };

  useEffect(() => { setCurrentPage(1); }, [activeTab, storeSearch]);

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => setActiveTab("users")}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => setActiveTab("stores")}>Stores</button>
        </div>
        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      <main className="admin-main">
        {/* Requirement: Dashboard stats for Users, Stores, and Ratings [cite: 19, 20, 21] */}
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers || users.length}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores || stores.length}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            <input 
              className="admin-search" 
              placeholder={`Search ${activeTab}...`} 
              value={storeSearch} 
              onChange={(e) => setStoreSearch(e.target.value)} 
            />
          </div>

          <div className="admin-table-wrapper">
            <ul className="admin-list">
              {paginatedData.map((item) => (
                <li key={activeTab === "users" ? item.user_id : item.store_id} className="admin-list-item">
                  <div className="item-main-info">
                    <span className="item-name">{item.name}</span>
                    <span className="item-sub">
                      {/* Requirement: View Name, Email, Address, Role [cite: 31, 32] */}
                      {item.email} • {activeTab === "users" ? item.role : item.address}
                    </span>
                  </div>
                  {/* Requirement: Show Rating if user is Store Owner or looking at Stores [cite: 30, 35] */}
                  {(activeTab === "stores" || item.role === "Store Owner") && (
                    <div className="item-rating-info">
                      ★ {Number(item.average_rating || item.rating || 0).toFixed(1)}
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>

          {totalPages > 1 && (
            <div className="admin-pagination">
              <button onClick={() => goToPage(currentPage - 1)} disabled={currentPage === 1}>Prev</button>
              <span className="page-indicator">Page {currentPage} of {totalPages}</span>
              <button onClick={() => goToPage(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default AdminDashboard;
