import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [showModal, setShowModal] = useState(null); // 'user' or 'store'
  
  // Form States
  const [userForm, setUserForm] = useState({ name: '', email: '', password: '', address: '', role: 'Normal User' });
  const [storeForm, setStoreForm] = useState({ name: '', email: '', address: '' });
  
  const [currentPage, setCurrentPage] = useState(1);
  const [storeSearch, setStoreSearch] = useState("");
  const navigate = useNavigate();
  const itemsPerPage = 5;

  const loadData = () => {
    api.get("/users").then((r) => setUsers(r.data)).catch(() => {});
    api.get("/stores").then((r) => setStores(r.data)).catch(() => {});
    api.get("/admin/stats").then((r) => setStats(r.data)).catch(() => {});
  };

  useEffect(() => { loadData(); }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Requirement: Add new users (Admin/Normal)
  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      await api.post("/users", userForm);
      setShowModal(null);
      loadData();
      alert("User Created Successfully");
    } catch (err) { alert(err.response?.data?.message || "Creation Failed"); }
  };

  // Requirement: Add new stores
  const handleCreateStore = async (e) => {
    e.preventDefault();
    try {
      await api.post("/stores", storeForm);
      setShowModal(null);
      loadData();
      alert("Store Created Successfully");
    } catch (err) { alert("Creation Failed"); }
  };

  const filteredData = useMemo(() => {
    const list = activeTab === "users" ? users : stores;
    return list.filter(item => 
      item.name.toLowerCase().includes(storeSearch.trim().toLowerCase()) ||
      item.email.toLowerCase().includes(storeSearch.trim().toLowerCase()) ||
      (item.address && item.address.toLowerCase().includes(storeSearch.trim().toLowerCase()))
    );
  }, [users, stores, activeTab, storeSearch]);

  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      
      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => setActiveTab("users")}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => setActiveTab("stores")}>Stores</button>
        </div>
        
        {/* Quick Actions */}
        <div className="admin-actions">
          <button onClick={() => setShowModal('user')} className="action-btn">+ Add User</button>
          <button onClick={() => setShowModal('store')} className="action-btn">+ Add Store</button>
        </div>

        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      <main className="admin-main">
        {/* Requirement: Dashboard Stats */}
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "All Users" : "All Stores"}</h2>
            <input 
              className="admin-search" 
              placeholder="Search by name, email, or address..." 
              value={storeSearch} 
              onChange={(e) => setStoreSearch(e.target.value)} 
            />
          </div>

          <ul className="admin-list">
            {paginatedData.map((item) => (
              <li key={activeTab === "users" ? item.user_id : item.store_id} className="admin-list-item">
                <div className="item-main-info">
                  <span className="item-name">{item.name}</span>
                  <span className="item-sub">{item.email} • {activeTab === "users" ? item.role : item.address}</span>
                  <span className="item-address-detail">{activeTab === "users" ? item.address : ""}</span>
                </div>
                {/* Requirement: Show rating for Stores and Store Owners */}
                {(activeTab === "stores" || item.role === "Store Owner") && (
                  <div className="item-rating-info">
                    ★ {Number(item.average_rating || item.rating || 0).toFixed(1)}
                  </div>
                )}
              </li>
            ))}
          </ul>
        </section>
      </main>

      {/* Creation Modals */}
      {showModal === 'user' && (
        <div className="modal-overlay">
          <div className="admin-glass-card modal-content">
            <h3>Add New User</h3>
            <form onSubmit={handleCreateUser} className="admin-form">
              <input placeholder="Full Name" onChange={e => setUserForm({...userForm, name: e.target.value})} required minLength={20} maxLength={60} />
              <input type="email" placeholder="Email" onChange={e => setUserForm({...userForm, email: e.target.value})} required />
              <input type="password" placeholder="Password" onChange={e => setUserForm({...userForm, password: e.target.value})} required minLength={8} maxLength={16} />
              <input placeholder="Address" onChange={e => setUserForm({...userForm, address: e.target.value})} maxLength={400} />
              <select onChange={e => setUserForm({...userForm, role: e.target.value})}>
                <option value="Normal User">Normal User</option>
                <option value="System Administrator">Admin</option>
                <option value="Store Owner">Store Owner</option>
              </select>
              <div className="modal-btns">
                <button type="submit" className="confirm-btn">Create</button>
                <button type="button" onClick={() => setShowModal(null)} className="cancel-btn">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showModal === 'store' && (
        <div className="modal-overlay">
          <div className="admin-glass-card modal-content">
            <h3>Add New Store</h3>
            <form onSubmit={handleCreateStore} className="admin-form">
              <input placeholder="Store Name" onChange={e => setStoreForm({...storeForm, name: e.target.value})} required />
              <input type="email" placeholder="Store Email" onChange={e => setStoreForm({...storeForm, email: e.target.value})} required />
              <input placeholder="Store Address" onChange={e => setStoreForm({...storeForm, address: e.target.value})} maxLength={400} />
              <div className="modal-btns">
                <button type="submit" className="confirm-btn">Create</button>
                <button type="button" onClick={() => setShowModal(null)} className="cancel-btn">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
