import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <>
      <style>{`
        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }

        body, html, #root {
          margin: 0; 
          padding: 0; 
          height: 100%;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          background: linear-gradient(135deg, #e0e7ff 0%, #fefefe 100%);
        }

        .container {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
          animation: fadeInUp 1s ease forwards;
          color: #1e293b;
          text-align: center;
          padding: 0 1rem;
        }

        .card {
          background: white;
          padding: 2.5rem 3rem;
          border-radius: 18px;
          box-shadow: 0 12px 34px rgba(0, 0, 0, 0.1);
          max-width: 450px;
          width: 100%;
          animation: fadeInUp 1.2s ease forwards;
        }

        h1 {
          margin-bottom: 0.25rem;
          font-size: 2.6rem;
          font-weight: 900;
          letter-spacing: 1px;
          color: #334155;
          text-transform: uppercase;
        }

        p.description {
          margin-bottom: 2.2rem;
          font-size: 1.1rem;
          color: #64748b;
          font-weight: 500;
          line-height: 1.6;
        }

        .button-group {
          display: flex;
          justify-content: center;
          gap: 1.2rem;
        }

        .btn {
          display: inline-block;
          font-weight: 700;
          text-decoration: none;
          padding: 0.85rem 2.2rem;
          border-radius: 12px;
          border: none;
          transition: all 0.3s ease;
          cursor: pointer;
          box-shadow: 0 6px 20px rgba(86, 105, 255, 0.3);
          user-select: none;
          font-size: 1.1rem;
        }

        .btn-login {
          background: linear-gradient(45deg, #4f46e5, #6366f1);
          color: white;
          box-shadow: 0 6px 20px rgba(95, 99, 242, 0.6);
        }
        .btn-login:hover {
          background: linear-gradient(45deg, #6366f1, #312e81);
          box-shadow: 0 8px 30px rgba(68, 66, 202, 0.8);
          transform: translateY(-3px);
        }

        .btn-signup {
          background: transparent;
          color: #4f46e5;
          border: 2.5px solid #4f46e5;
          box-shadow: none;
        }
        .btn-signup:hover {
          background: #4f46e5;
          color: white;
          box-shadow: 0 8px 30px rgba(95, 99, 242, 0.6);
          transform: translateY(-3px);
        }

        footer {
          margin-top: 3rem;
          color: #94a3b8;
          font-weight: 500;
          font-size: 0.96rem;
          letter-spacing: 0.4px;
          animation: fadeInUp 1.5s ease forwards;
        }
      `}</style>

      <div className="container">
        <div className="card">
          <h1>Store Rating App</h1>
          <p className="description">
            Welcome! Discover and rate stores, or manage your business ratings with a robust feedback system.
          </p>
          <div className="button-group">
            <Link to="/login" className="btn btn-login">Login</Link>
            <Link to="/signup" className="btn btn-signup">Signup</Link>
          </div>
        </div>
        <footer>&copy; {new Date().getFullYear()} Store Rating App | Built for your success</footer>
      </div>
    </>
  );
};

export default HomePage;
