import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create'); // 'create' or 'stores'

  const load = () =>
    api
      .get('/stores')
      .then((r) => setStores(r.data))
      .catch(() => {});

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      load();
      setActiveTab('stores'); // Switch to stores after creating
    } catch (e) {}
  };

  return (
    <>
      <style>{`
        body, html, #root {
          margin: 0;
          height: 100%;
          font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
          Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          background-color: #f4f7fc;
          color: #222;
        }

        .dashboard-wrapper {
          display: flex;
          height: 100vh;
          overflow: hidden;
          background: #fff;
        }

        nav.sidebar {
          width: 260px;
          background-color: #7f56da;
          color: white;
          display: flex;
          flex-direction: column;
          padding: 60px 0;
          font-weight: 700;
          font-size: 1.15rem;
          user-select: none;
          position: fixed;
          top: 0;
          left: 0;
          bottom: 0;
          z-index: 1000;
        }

        nav.sidebar button {
          background: none;
          border: none;
          color: inherit;
          padding: 20px 32px;
          width: 100%;
          text-align: left;
          cursor: pointer;
          transition: background-color 0.3s, color 0.3s;
          font-weight: 600;
          font-size: 1.15rem;
        }

        nav.sidebar button:hover,
        nav.sidebar button.active {
          background-color: #5b3bb5;
          color: #dcdcff;
        }

        main.content {
          margin-left: 260px;
          padding: 60px 48px 40px 48px;
          flex-grow: 1;
          overflow-y: auto;
          height: 100vh;
          box-sizing: border-box;
          display: flex;
          flex-direction: column;
          justify-content: flex-start;
        }

        main.content h2 {
          font-weight: 900;
          font-size: 2rem;
          color: #2c2e3a;
          margin-top: 0;
          margin-bottom: 32px;
        }

        form.create-store-form {
          max-width: 480px;
          display: flex;
          flex-direction: column;
          animation: fadeSlideIn 0.8s ease forwards;
        }

        form.create-store-form h3 {
          font-weight: 700;
          font-size: 1.4rem;
          margin-bottom: 24px;
          padding-bottom: 8px;
          border-bottom: 3px solid #7f56da;
          color: #3b3d52;
          width: fit-content;
          user-select: none;
        }

        form.create-store-form input {
          padding: 14px 18px;
          margin-bottom: 24px;
          font-size: 1rem;
          border-radius: 10px;
          border: 1.8px solid #ddd;
          background-color: #fbfbfd;
          transition: border-color 0.3s ease;
          outline-offset: 2px;
        }

        form.create-store-form input:focus {
          border-color: #7f56da;
          background-color: #fff;
          box-shadow: 0 0 8px #b99aff88;
        }

        form.create-store-form button {
          background-color: #7f56da;
          color: white;
          font-weight: 700;
          padding: 15px 0;
          font-size: 1.2rem;
          border-radius: 12px;
          border: none;
          cursor: pointer;
          box-shadow: 0 8px 15px rgb(127 86 218 / 0.3);
          transition: background-color 0.3s ease, transform 0.15s ease;
        }

        form.create-store-form button:hover {
          background-color: #593abf;
          transform: translateY(-2px);
          box-shadow: 0 10px 20px rgb(89 58 191 / 0.4);
        }

        section.stores-list-section {
          animation: fadeSlideIn 0.8s ease forwards;
          max-width: 800px;
        }

        ul.stores-list {
          list-style: none;
          padding: 0;
          margin: 0;
          max-height: 520px;
          overflow-y: auto;
          display: grid;
          grid-template-columns: repeat(auto-fill,minmax(260px, 1fr));
          gap: 20px;
        }

        ul.stores-list li {
          background: #fafbff;
          border-radius: 14px;
          box-shadow: 0 6px 20px rgba(127, 110, 218, 0.07);
          padding: 20px 24px;
          font-weight: 600;
          font-size: 1.1rem;
          color: #494553;
          display: flex;
          justify-content: space-between;
          align-items: center;
          cursor: default;
          transition: box-shadow 0.3s ease;
          user-select: none;
        }

        ul.stores-list li:hover {
          box-shadow: 0 12px 32px rgba(127, 86, 218, 0.25);
        }

        ul.stores-list li span {
          font-weight: 400;
          font-size: 0.9rem;
          color: #7f56da;
          user-select: text;
        }

        @keyframes fadeSlideIn {
          0% {
            opacity: 0;
            transform: translateX(15px);
          }
          100% {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @media (max-width: 768px) {
          nav.sidebar {
            position: relative;
            width: 100%;
            height: auto;
            display: flex;
            flex-direction: row;
            padding: 8px 0;
            justify-content: space-around;
          }
          nav.sidebar button {
            padding: 14px 8px;
            font-size: 1rem;
            flex-grow: 1;
            text-align: center;
          }
          main.content {
            margin-left: 0;
            padding: 20px 24px 40px 24px;
            max-height: none;
          }
          ul.stores-list {
            grid-template-columns: 1fr !important;
            max-height: 320px;
          }
        }
      `}</style>

      <div className="dashboard-wrapper" role="main" aria-label="Store owner dashboard">
        <nav className="sidebar" aria-label="Dashboard Navigation">
          <button
            type="button"
            className={activeTab === 'create' ? 'active' : ''}
            onClick={() => setActiveTab('create')}
            aria-current={activeTab === 'create' ? 'page' : undefined}
          >
            Create Store
          </button>
          <button
            type="button"
            className={activeTab === 'stores' ? 'active' : ''}
            onClick={() => setActiveTab('stores')}
            aria-current={activeTab === 'stores' ? 'page' : undefined}
          >
            Your Stores
          </button>
        </nav>

        <main className="content">
          {activeTab === 'create' && (
            <form onSubmit={submit} className="create-store-form" aria-label="Create a new store form">
              <h2>Create Store</h2>
              <input
                type="text"
                placeholder="Name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                required
                aria-required="true"
              />
              <input
                type="email"
                placeholder="Email"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                required
                aria-required="true"
              />
              <input
                type="text"
                placeholder="Address"
                value={form.address}
                onChange={(e) => setForm({ ...form, address: e.target.value })}
              />
              <button type="submit">Create</button>
            </form>
          )}

          {activeTab === 'stores' && (
            <section className="stores-list-section" aria-label="List of your stores">
              <h2>Your Stores</h2>
              <ul className="stores-list" tabIndex={0}>
                {stores
                  .filter((s) => s.owner_id)
                  .map((s) => (
                    <li key={s.store_id}>
                      {s.name} <span>({s.email})</span>
                    </li>
                  ))}
                {stores.filter((s) => s.owner_id).length === 0 && <p>You have no stores yet.</p>}
              </ul>
            </section>
          )}
        </main>
      </div>
    </>
  );
};

export default OwnerDashboard;
