import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create'); // 'create' or 'stores'
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState('');

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  const load = async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      setStores(response.data);
    } catch (e) {
      // Optionally handle error here
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      await load();
      setActiveTab('stores');
      setCurrentPage(1);
      setAlert('Store created successfully!');
      // Remove alert after 3 seconds
      setTimeout(() => setAlert(''), 3000);
    } catch (e) {
      setAlert('Failed to add store. Please try again.');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  const ownerStores = stores.filter((s) => s.owner_id);
  const totalPages = Math.ceil(ownerStores.length / itemsPerPage);
  const paginatedStores = ownerStores.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );
  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  return (
    <>
      <style>{`
        body, html, #root {
          margin: 0;
          height: 100%;
          font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
            Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          background-color: #f4f7fc;
          color: #222;
        }

        .dashboard-wrapper {
          display: flex;
          height: 100vh;
          overflow: hidden;
          background: #fff;
          flex-direction: row;
        }

        nav.sidebar {
          width: 260px;
          min-width: 220px;
          background: linear-gradient(135deg, #7f56da, #7563fb 85%);
          color: #fff;
          display: flex;
          flex-direction: column;
          padding: 60px 0;
          font-weight: 600;
          font-size: 1.05rem;
          user-select: none;
          box-shadow: 2px 0 22px 0 #ede9fa;
          position: fixed;
          top: 0;
          left: 0;
          bottom: 0;
          z-index: 1000;
          border-radius: 0;
          overflow: hidden;
        }

        nav.sidebar button {
          background: transparent;
          border: none;
          color: #fff;
          padding: 12px 28px 12px 36px;
          width: 100%;
          text-align: left;
          cursor: pointer;
          transition: background 0.18s, color 0.22s, box-shadow 0.16s;
          font-weight: 600;
          font-size: 1.1rem;
          border-radius: 0;
          margin-bottom: 7px;
          outline: none;
          position: relative;
        }
        /* Rounded edges only for active menu */
        nav.sidebar button.active {
          background: #fff;
          color: #7f56da;
          box-shadow: 0 2px 14px 0 #7f56da16;
          font-weight: 700;
          border-radius: 12px 0 0 12px;
        }
        nav.sidebar button:not(.active):hover {
          background: rgba(255,255,255,0.15);
          color: #e5dbff;
        }

        main.content {
          flex-grow: 1;
          background: #fafbff;
          box-sizing: border-box;
          overflow-y: auto;
          padding: 48px 36px;
          display: flex;
          justify-content: center;
          align-items: flex-start;
          margin-left: 260px;
          height: 100vh;
          position: relative;
        }

        .center-wrapper {
          width: 100%;
          max-width: 520px;
          background: #fff;
          border-radius: 18px;
          box-shadow: 0 12px 40px rgba(127, 86, 218, 0.12);
          display: flex;
          flex-direction: column;
          animation: fadeSlideIn 0.8s cubic-bezier(0.4,0,0.2,1);
          padding: 48px 36px;
          position: relative;
        }

        form.create-store-form {
          width: 100%;
          display: flex;
          flex-direction: column;
        }

        form.create-store-form h2 {
          font-weight: 700;
          font-size: 2.1rem;
          color: #222a35;
          text-align: center;
          margin-bottom: 32px;
          margin-top: 0;
          user-select: none;
          letter-spacing: 1px;
        }

        form.create-store-form input {
          padding: 16px 18px;
          margin-bottom: 22px;
          font-size: 1.1rem;
          border-radius: 12px;
          border: 1.5px solid #ebe8fc;
          background-color: #f8faff;
          transition: border-color 0.23s;
          outline: none;
          font-weight: 400;
        }

        form.create-store-form input:focus {
          border-color: #7f56da;
          background-color: #fff;
          box-shadow: 0 0 10px #b99aff33;
        }

        form.create-store-form button {
          background: linear-gradient(90deg, #7f56da 30%, #7563fb 110%);
          color: #fff;
          font-weight: 700;
          padding: 15px 0;
          font-size: 1.17rem;
          border-radius: 13px;
          border: none;
          box-shadow: 0 4px 14px 0 #7f56da29;
          cursor: pointer;
          letter-spacing: 1px;
          transition: background 0.19s, box-shadow 0.19s, transform 0.15s;
        }

        form.create-store-form button:hover, form.create-store-form button:focus {
          background: linear-gradient(90deg, #6842c2 0%, #6842c2 90%);
          transform: translateY(-1.5px) scale(1.01);
          box-shadow: 0 10px 30px #7f56da22;
        }

        .stores-list-section {
          width: 100%;
          max-width: 700px;
        }

        .stores-list-section h2 {
          font-weight: 700;
          font-size: 2rem;
          color: #222a35;
          text-align: left;
          margin-bottom: 30px;
          margin-top: 0;
          user-select: none;
          letter-spacing: 1px;
        }

        ul.stores-list {
          list-style: none;
          padding: 0;
          margin: 0;
          max-height: 520px;
          overflow-y: auto;
          display: grid;
          grid-template-columns: 1fr;
          gap: 18px;
        }

        ul.stores-list li {
          background: #f4f7fc;
          border-radius: 11px;
          box-shadow: 0 2px 9px #7f56da10;
          padding: 18px 24px;
          font-weight: 500;
          font-size: 1.12rem;
          color: #7f56da;
          display: flex;
          justify-content: space-between;
          align-items: center;
          cursor: default;
          transition: box-shadow 0.2s;
          user-select: none;
        }

        ul.stores-list li:hover {
          box-shadow: 0 10px 28px #7f56da19;
        }

        ul.stores-list li span {
          font-weight: 400;
          font-size: 0.98rem;
          color: #7f56da;
          user-select: text;
        }

        .pagination {
          display: flex;
          justify-content: center;
          margin-top: 22px;
          gap: 8px;
          flex-wrap: wrap;
          user-select: none;
        }

        .pagination button {
          background: #7f56da;
          border: none;
          border-radius: 8px;
          color: white;
          font-weight: 600;
          font-size: 1rem;
          padding: 6px 14px;
          cursor: pointer;
          transition: background-color 0.2s ease;
        }

        .pagination button[disabled] {
          background: #bda8f9;
          cursor: not-allowed;
        }

        .pagination button:hover:not([disabled]) {
          background: #6842c2;
        }

        .spinner-overlay {
          position: absolute;
          top: 0; bottom: 0; left: 0; right: 0;
          background: rgba(255, 255, 255, 0.7);
          display: flex;
          justify-content: center;
          align-items: center;
          border-radius: 18px;
          z-index: 100;
        }

        .spinner {
          border: 6px solid #e0e0e0;
          border-top: 6px solid #7f56da;
          border-radius: 50%;
          width: 48px;
          height: 48px;
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .alert-box {
          position: fixed;
          top: 18px;
          right: 18px;
          background-color: #7f56da;
          color: white;
          padding: 14px 20px;
          border-radius: 14px;
          box-shadow: 0 6px 18px rgba(127, 86, 218, 0.4);
          font-weight: 600;
          user-select: none;
          z-index: 10000;
          min-width: 220px;
          text-align: center;
        }

        @media (max-width: 800px) {
          main.content {
            margin-left: 0;
            padding: 24px 12px 40px;
          }
          nav.sidebar {
            position: relative;
            width: 100%;
            flex-direction: row;
            height: auto;
            min-width: 0;
            justify-content: center;
            align-items: center;
            box-shadow: none;
            padding: 12px 0;
            border-radius: 0;
          }
          nav.sidebar button {
            padding: 12px 0;
            font-size: 1.02rem;
            flex-grow: 1;
            border-left: none;
            border-bottom: 3px solid transparent;
            text-align: center;
            border-radius: 0;
            margin-bottom: 0;
          }
          nav.sidebar button.active, nav.sidebar button:hover {
            border-left: none;
            border-bottom: 3px solid #7f56da;
          }
          .dashboard-wrapper {
            flex-direction: column;
            height: 100vh;
          }
        }
      `}</style>

      <div className="dashboard-wrapper" role="main" aria-label="Store owner dashboard">
        <nav className="sidebar" aria-label="Dashboard Navigation">
          <button
            type="button"
            className={activeTab === 'create' ? 'active' : ''}
            onClick={() => setActiveTab('create')}
            aria-current={activeTab === 'create' ? 'page' : undefined}
          >
            Create Store
          </button>
          <button
            type="button"
            className={activeTab === 'stores' ? 'active' : ''}
            onClick={() => setActiveTab('stores')}
            aria-current={activeTab === 'stores' ? 'page' : undefined}
          >
            Your Stores
          </button>
        </nav>

        <main className="content" aria-live="polite" aria-busy={loading}>
          {activeTab === 'create' && (
            <div className="center-wrapper">
              <form
                onSubmit={submit}
                className="create-store-form"
                aria-label="Create a new store form"
              >
                <h2>Create Store</h2>
                <input
                  type="text"
                  placeholder="Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                  aria-required="true"
                  disabled={loading}
                />
                <input
                  type="email"
                  placeholder="Email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                  aria-required="true"
                  disabled={loading}
                />
                <input
                  type="text"
                  placeholder="Address"
                  value={form.address}
                  onChange={(e) => setForm({ ...form, address: e.target.value })}
                  disabled={loading}
                />
                <button type="submit" disabled={loading}>
                  Create
                </button>
              </form>
              {loading && (
                <div className="spinner-overlay" aria-label="Loading">
                  <div className="spinner"></div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'stores' && (
            <div className="center-wrapper" style={{ position: 'relative' }}>
              {loading && (
                <div className="spinner-overlay" aria-label="Loading">
                  <div className="spinner"></div>
                </div>
              )}
              <section className="stores-list-section" aria-label="List of your stores">
                <h2>Your Stores</h2>
                <ul className="stores-list" tabIndex={0}>
                  {paginatedStores.map((s) => (
                    <li key={s.store_id}>
                      {s.name} <span>({s.email})</span>
                    </li>
                  ))}
                  {ownerStores.length === 0 && <p>You have no stores yet.</p>}
                </ul>

                {ownerStores.length > itemsPerPage && (
                  <div className="pagination" role="navigation" aria-label="Pagination Navigation">
                    <button
                      onClick={() => goToPage(currentPage - 1)}
                      disabled={currentPage === 1 || loading}
                      aria-label="Previous page"
                    >
                      &larr; Prev
                    </button>
                    {[...Array(totalPages)].map((_, idx) => {
                      const page = idx + 1;
                      return (
                        <button
                          key={page}
                          onClick={() => goToPage(page)}
                          aria-current={page === currentPage ? 'page' : undefined}
                          style={{
                            fontWeight: page === currentPage ? 'bold' : 'normal',
                            backgroundColor: page === currentPage ? '#6842c2' : undefined,
                          }}
                          disabled={loading}
                        >
                          {page}
                        </button>
                      );
                    })}
                    <button
                      onClick={() => goToPage(currentPage + 1)}
                      disabled={currentPage === totalPages || loading}
                      aria-label="Next page"
                    >
                      Next &rarr;
                    </button>
                  </div>
                )}
              </section>
            </div>
          )}
        </main>

        {alert && (
          <div role="alert" className="alert-box" aria-live="assertive">
            {alert}
          </div>
        )}
      </div>
    </>
  );
};

export default OwnerDashboard;
