import React, { useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await api.post('/auth/login', formData);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      const role = res.data.user.role;
      if (role === 'System Administrator') navigate('/admin');
      else if (role === 'Store Owner') navigate('/owner');
      else navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <style>{`
        body, html, #root {
          margin: 0;
          padding: 0;
          height: 100%;
          background-color: #f4f6f8;
          font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen,
            Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .card {
  background: #fff;
  padding: 4rem 3.5rem;
  border-radius: 16px;
  box-shadow: 0 14px 35px rgba(0, 0, 0, 0.12);
  width: 100%;
  width: 1050px!i; /* increased width */
  box-sizing: border-box;
  text-align: center;
  min-height: 480px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

        h2 {
          margin-bottom: 2rem; /* more spacing below header */
          font-weight: 900;
          letter-spacing: 0.06em;
          color: #2c3e50;
          user-select: none;
        }
        .error {
          background-color: #ffdddd;
          color: #d8000c;
          padding: 0.8rem 1rem;
          margin-bottom: 1rem;
          border-radius: 8px;
          font-weight: 600;
          user-select: none;
        }
        input[type="email"], input[type="password"] {
          width: 100%;
          padding: 1.2rem 1.2rem; /* increased input height */
          margin-bottom: 1.5rem;
          border-radius: 12px;
          border: 1.5px solid #ced4da;
          font-size: 1.1rem; /* slightly bigger font */
          transition: border-color 0.3s ease;
          box-sizing: border-box;
        }
        input[type="email"]:focus, input[type="password"]:focus {
          border-color: #5750de;
          outline: none;
          box-shadow: 0 0 8px rgba(87, 80, 222, 0.4);
        }
        button {
          width: 100%;
          padding: 1.3rem 0; /* taller button */
          font-size: 1.25rem; /* larger text */
          font-weight: 700;
          color: white;
          background: linear-gradient(45deg, #5750de, #7563fb);
          border: none;
          border-radius: 14px;
          cursor: pointer;
          box-shadow: 0 12px 40px rgba(87, 80, 222, 0.35);
          transition: all 0.3s ease;
          user-select: none;
        }
        button:hover:not(:disabled) {
          background: linear-gradient(45deg, #7563fb, #5750de);
          box-shadow: 0 14px 48px rgba(117, 99, 251, 0.45);
          transform: translateY(-4px);
        }
        button:disabled {
          background: #a3a0f7;
          box-shadow: none;
          cursor: default;
        }
      `}</style>

      <form onSubmit={onSubmit} className="card" aria-label="Login form">
        <h2>Login</h2>
        {error && <div className="error" role="alert">{error}</div>}
        <div>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={onChange}
            required
            aria-required="true"
            aria-label="Email address"
          />
        </div>
        <div>
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={onChange}
            required
            aria-required="true"
            aria-label="Password"
          />
        </div>
        <button type="submit" disabled={loading} aria-busy={loading}>
          {loading ? 'Signing in...' : 'Login'}
        </button>
      </form>
    </>
  );
};

export default Login;
