import React, { useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import "./Login.css"

const roles = ['Normal User', 'Store Owner'];

const Signup = () => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '', address: '', role: 'Normal User' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await api.post('/auth/register', formData);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed');
    } finally { setLoading(false); }
  };

  return (
    <form onSubmit={onSubmit} className="card">
      <h2>Signup</h2>
      {error && <div className="error">{error}</div>}
      <input name="name" placeholder="Name" value={formData.name} onChange={onChange} required />
      <input name="email" type="email" placeholder="Email" value={formData.email} onChange={onChange} required />
      <input name="password" type="password" placeholder="Password" value={formData.password} onChange={onChange} required minLength={8} />
      <input name="address" placeholder="Address" value={formData.address} onChange={onChange} />
      <select name="role" value={formData.role} onChange={onChange}>
        {roles.map(r => <option key={r} value={r}>{r}</option>)}
      </select>
      <button disabled={loading}>{loading ? 'Creating...' : 'Create Account'}</button>
    </form>
  );
};

export default Signup;
