import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });

  const load = () =>
    api
      .get('/stores')
      .then((r) => setStores(r.data))
      .catch(() => {});

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      load();
    } catch (e) {}
  };

  return (
    <>
      <style>{`
        body {
          background-color: #f4f7fc;
          font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
          Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          color: #222;
          margin: 0;
          padding: 0;
        }

        .dashboard-container {
          max-width: 960px;
          margin: 40px auto 64px auto;
          padding: 0 24px;
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 48px;
        }

        h1.dashboard-title {
          grid-column: 1 / -1;
          font-weight: 900;
          font-size: 2.8rem;
          margin-bottom: 32px;
          text-align: center;
          color: #2c2e3a;
        }

        form.card {
          background: #ffffff;
          border-radius: 16px;
          box-shadow: 0 12px 30px rgba(50, 50, 93, 0.1),
            0 4px 15px rgba(0, 0, 0, 0.07);
          padding: 36px 28px;
          display: flex;
          flex-direction: column;
          animation: slideIn 0.8s ease forwards;
        }

        form.card h3 {
          font-weight: 700;
          margin-bottom: 24px;
          font-size: 1.4rem;
          color: #3b3d52;
          border-bottom: 2px solid #7f56da;
          padding-bottom: 8px;
          width: fit-content;
        }

        form.card input {
          padding: 14px 18px;
          margin-bottom: 24px;
          font-size: 1rem;
          border-radius: 10px;
          border: 1.8px solid #ddd;
          background-color: #fbfbfd;
          transition: border-color 0.3s ease;
          outline-offset: 2px;
        }
        form.card input:focus {
          border-color: #7f56da;
          background-color: #fff;
          box-shadow: 0 0 8px #b99aff88;
        }

        form.card button {
          background-color: #7f56da;
          color: white;
          font-weight: 700;
          padding: 15px 0;
          font-size: 1.2rem;
          border-radius: 12px;
          border: none;
          cursor: pointer;
          box-shadow: 0 8px 15px rgb(127 86 218 / 0.3);
          transition: background-color 0.3s ease, transform 0.15s ease;
        }
        form.card button:hover {
          background-color: #593abf;
          transform: translateY(-2px);
          box-shadow: 0 10px 20px rgb(89 58 191 / 0.4);
        }

        section.stores {
          display: flex;
          flex-direction: column;
          gap: 20px;
        }

        h2.stores-title {
          font-weight: 700;
          font-size: 1.8rem;
          margin-bottom: 12px;
          border-left: 4px solid #7f56da;
          padding-left: 12px;
          color: #262730;
        }

        ul.stores-list {
          list-style: none;
          padding: 0;
          margin: 0;
          max-height: 400px;
          overflow-y: auto;
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 20px;
        }

        ul.stores-list li {
          background: white;
          border-radius: 16px;
          box-shadow: 0 6px 20px rgba(0, 0, 0, 0.05);
          padding: 18px 22px;
          font-size: 1.05rem;
          font-weight: 600;
          color: #444654;
          transition: box-shadow 0.3s ease;
          cursor: default;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }
        ul.stores-list li:hover {
          box-shadow: 0 12px 30px rgba(127, 86, 218, 0.25);
        }

        ul.stores-list li span {
          font-weight: 400;
          font-size: 0.9rem;
          color: #7f56da;
          user-select: text;
        }

        @keyframes slideIn {
          0% {
            transform: translateX(-40px);
            opacity: 0;
          }
          100% {
            transform: translateX(0);
            opacity: 1;
          }
        }

        /* Responsive */
        @media (max-width: 820px) {
          .dashboard-container {
            display: block;
            max-width: 480px;
            padding: 0 20px;
            margin: 32px auto 48px;
          }
          ul.stores-list {
            grid-template-columns: 1fr !important;
            max-height: 300px;
          }
        }
      `}</style>

      <div className="dashboard-container">
        <h1 className="dashboard-title">Store Owner Dashboard</h1>

        <form onSubmit={submit} className="card">
          <h3>Create Store</h3>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />
          <input
            placeholder="Address"
            value={form.address}
            onChange={(e) => setForm({ ...form, address: e.target.value })}
          />
          <button type="submit">Create</button>
        </form>

        <section className="stores">
          <h2 className="stores-title">Your Stores</h2>
          <ul className="stores-list" aria-label="List of your stores">
            {stores
              .filter((s) => s.owner_id)
              .map((s) => (
                <li key={s.store_id}>
                  {s.name} <span>({s.email})</span>
                </li>
              ))}
          </ul>
        </section>
      </div>
    </>
  );
};

export default OwnerDashboard;
