import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const UserDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratingSubmitting, setRatingSubmitting] = useState(null);
  const load = () => api.get('/stores').then(r => setStores(r.data)).catch(()=>{});
  useEffect(() => { load(); }, []);

  const rate = async (storeId, rating) => {
    setRatingSubmitting(storeId);
    try { await api.post(`/ratings/${storeId}`, { rating }); load(); } catch(e){} finally { setRatingSubmitting(null); }
  };

  return (
    <div>
      <h1>Your Dashboard</h1>
      <ul>
        {stores.map(s => (
          <li key={s.store_id} style={{marginBottom:'0.75rem'}}>
            <strong>{s.name}</strong> avg {Number(s.average_rating).toFixed(1)} ({s.rating_count})
            <div>
              {[1,2,3,4,5].map(n => (
                <button key={n} disabled={ratingSubmitting===s.store_id} onClick={()=>rate(s.store_id,n)}>{n}</button>
              ))}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserDashboard;
