import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <>
      {/* Google Fonts */}
      <link
        href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;900&display=swap"
        rel="stylesheet"
      />
      <style>{`
        * {
          box-sizing: border-box;
        }
        body, html, #root {
          margin: 0; 
          padding: 0; 
          height: 100%;
          font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen,
            Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          background: linear-gradient(135deg, #667eea, #764ba2); /* Attractive purple-blue gradient */
          overflow-x: hidden;
        }

        @keyframes fadeZoomIn {
          0% {
            opacity: 0;
            transform: scale(0.92);
          }
          100% {
            opacity: 1;
            transform: scale(1);
          }
        }

        .container {
          min-height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
          padding: 0 1rem;
          animation: fadeZoomIn 1s ease forwards;
          color: white; /* Light text on dark background */
          text-align: center;
        }

        .card {
          background: rgba(255, 255, 255, 0.1); /* Transparent glass effect */
          padding: 3rem 3.5rem;
          border-radius: 20px;
          max-width: 460px;
          width: 100%;
          box-shadow:
            0 4px 15px rgba(0,0,0,0.2);
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
          will-change: transform;
          transition: transform 0.3s ease;
        }
        .card:hover {
          transform: translateY(-6px);
          box-shadow:
            0 8px 30px rgba(0, 0, 0, 0.35);
        }

        h1 {
          font-weight: 900;
          font-size: 2.8rem;
          margin-bottom: 0.35rem;
          letter-spacing: 0.05em;
          text-transform: uppercase;
          user-select: none;
          color: #f3f4f6;
          text-shadow: 1px 1px 3px rgba(0,0,0,0.3);
        }

        p.description {
          margin-bottom: 2.7rem;
          font-size: 1.15rem;
          font-weight: 500;
          line-height: 1.55;
          user-select: none;
          color: #d1d5db;
          text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
        }

        .button-group {
          display: flex;
          justify-content: center;
          gap: 1.6rem;
          flex-wrap: nowrap; /* Keep buttons on same line */
        }

        .btn {
          display: inline-flex;
          align-items: center;
          justify-content: center;
          font-weight: 700;
          border-radius: 14px;
          padding: 0.95rem 2.6rem;
          font-size: 1.12rem;
          text-decoration: none;
          cursor: pointer;
          transition: all 0.28s cubic-bezier(0.4, 0, 0.2, 1);
          user-select: none;
          border: none;
          min-width: 130px;
          gap: 0.6rem;
          box-shadow: 0 10px 24px rgba(0,0,0,0.15);
        }

        .btn-login {
          background: linear-gradient(45deg, #5a67d8, #4c51bf);
          color: white;
          box-shadow: 0 10px 30px rgba(74,85,175,0.5);
          border: none;
          text-shadow: 0 1px 1px rgba(0,0,0,0.3);
        }
        .btn-login:hover {
          background: linear-gradient(45deg, #4c51bf, #5a67d8);
          box-shadow: 0 14px 40px rgba(74,85,175,0.75);
          transform: translateY(-4px);
        }

        .btn-signup {
          background: transparent;
          color: #a3bffa;
          border: 2.5px solid #a3bffa;
          box-shadow: none;
          text-shadow: 0 1px 1px rgba(0,0,0,0.25);
        }
        .btn-signup:hover {
          background: #a3bffa;
          color: #2c5282;
          box-shadow: 0 14px 40px rgba(163,191,250,0.75);
          transform: translateY(-4px);
        }

        footer {
          margin-top: 3rem;
          font-weight: 500;
          color: #cbd5e1;
          letter-spacing: 0.06em;
          user-select: none;
          font-size: 0.95rem;
          text-align: center;
          text-shadow: 0 1px 1px rgba(0,0,0,0.15);
        }

        @media (max-width: 480px) {
          .card {
            padding: 2.5rem 2rem;
          }
          .btn {
            min-width: 115px;
            font-size: 1rem;
          }
          .button-group {
            gap: 1rem;
          }
        }
      `}</style>

      <main className="container" role="main" aria-label="Home page welcome and navigation">
        <section className="card" aria-labelledby="homepage-welcome">
          <h1 id="homepage-welcome">Store Rating App</h1>
          <p className="description">
            Welcome! Discover, rate, and manage stores with rich feedback and seamless user experience.
          </p>
          <nav className="button-group" aria-label="Authentication actions">
            <Link to="/login" className="btn btn-login" aria-label="Login to your account">
              Login
            </Link>
            <Link to="/signup" className="btn btn-signup" aria-label="Create a new account">
              Signup
            </Link>
          </nav>
        </section>

        <footer>
          &copy; {new Date().getFullYear()} Store Rating App â€” Crafted for your success
        </footer>
      </main>
    </>
  );
};

export default HomePage;
