import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    address: "",
    role: "Normal User",
  });

  const loadData = async () => {
    try {
      const [uRes, sRes, statsRes] = await Promise.all([
        api.get("/users"),
        api.get("/stores"),
        api.get("/users/stats")
      ]);

      // Robust data extraction
      const extract = (res) => (Array.isArray(res.data) ? res.data : (res.data.rows || res.data.users || res.data.stores || []));
      
      setUsers(extract(uRes));
      setStores(extract(sRes));
      setStats(statsRes.data || { totalUsers: 0, totalStores: 0, totalRatings: 0 });
    } catch (err) {
      console.error("Failed to load data:", err);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login'); // Requirement: Can log out from system
  };

  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      if (activeTab === "users") {
        await api.post("/auth/register", formData);
      } else {
        await api.post("/stores", { 
          name: formData.name, 
          email: formData.email, 
          address: formData.address 
        });
      }
      setShowModal(false);
      setFormData({ name: "", email: "", password: "", address: "", role: "Normal User" });
      loadData();
      alert("Added successfully!");
    } catch (err) {
      alert(err.response?.data?.message || "Operation failed.");
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm("Permanently delete this user?")) {
      try {
        await api.delete(`/users/${userId}`);
        loadData();
      } catch (err) {
        alert("Delete failed");
      }
    }
  };

  const filteredData = useMemo(() => {
    const data = activeTab === "users" ? users : stores;
    const term = search.toLowerCase();
    return data.filter(item => 
      item.name?.toLowerCase().includes(term) ||
      item.email?.toLowerCase().includes(term) ||
      item.address?.toLowerCase().includes(term) ||
      (activeTab === "users" && item.role?.toLowerCase().includes(term))
    );
  }, [users, stores, activeTab, search]);

  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      
      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => { setActiveTab("users"); setCurrentPage(1); }}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => { setActiveTab("stores"); setCurrentPage(1); }}>Stores</button>
        </div>
        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      <main className="admin-main">
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            <div className="header-actions">
              <input className="admin-search" placeholder="Filter records..." value={search} onChange={(e) => setSearch(e.target.value)} />
              <button className="action-btn" onClick={() => setShowModal(true)}>+ Add {activeTab === "users" ? "User" : "Store"}</button>
            </div>
          </div>

          <div className="table-container">
            <table className="admin-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Address</th>
                  <th>{activeTab === "users" ? "Role" : "Status"}</th>
                  <th>Rating</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
{/* Replace the <tbody> section in your AdminDashboard.js */}
<tbody>
  {paginatedData.length === 0 ? (
    <tr><td colSpan="6" className="empty-state">No records found.</td></tr>
  ) : (
    paginatedData.map((item) => (
      <tr key={activeTab === "users" ? item.user_id : item.store_id} className="table-row">
        <td className="font-bold cell-name">{item.name}</td>
        <td className="text-muted cell-email">{item.email}</td>
        <td className="text-muted truncate-cell cell-address">{item.address || "—"}</td>
        <td className="cell-role">
          <span className={`badge ${item.role?.replace(/\s+/g, '-').toLowerCase() || 'store-badge'}`}>
            {activeTab === "users" ? item.role : "Registered Store"}
          </span>
        </td>
        <td className="rating-cell text-center">
          {/* Requirement: Only show rating for Store Owner  */}
          {(activeTab === "stores" || item.role === "Store Owner") ? (
            <span className="rating-pill">
              ★ {Number(item.average_rating || item.rating || 0).toFixed(1)}
            </span>
          ) : (
            <span className="dash-placeholder">—</span>
          )}
        </td>
        <td className="text-center cell-actions">
          {activeTab === "users" && (
            <button onClick={() => handleDeleteUser(item.user_id)} className="icon-delete-btn" title="Delete User">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <polyline points="3 6 5 6 21 6"></polyline>
                <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path>
              </svg>
            </button>
          )}
        </td>
      </tr>
    ))
  )}
</tbody>
            </table>
          </div>

          {filteredData.length > itemsPerPage && (
            <div className="admin-pagination">
              <button onClick={() => setCurrentPage(p => Math.max(1, p - 1))} disabled={currentPage === 1}>Prev</button>
              <span className="page-indicator">Page {currentPage}</span>
              <button onClick={() => setCurrentPage(p => p + 1)} disabled={paginatedData.length < itemsPerPage}>Next</button>
            </div>
          )}
        </section>
      </main>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Add New {activeTab === "users" ? "User" : "Store"}</h3>
            <form onSubmit={handleAddSubmit} className="admin-form">
              <input placeholder="Name (20-60 chars)" required value={formData.name} onChange={(e) => setFormData({...formData, name: e.target.value})} />
              <input type="email" placeholder="Email" required value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} />
              {activeTab === "users" && (
                <>
                  <input type="password" placeholder="Password" required onChange={(e) => setFormData({...formData, password: e.target.value})} />
                  <select value={formData.role} onChange={(e) => setFormData({...formData, role: e.target.value})}>
                    <option value="Normal User">Normal User</option>
                    <option value="Store Owner">Store Owner</option>
                    <option value="System Administrator">Admin</option>
                  </select>
                </>
              )}
              <textarea placeholder="Address" rows="3" value={formData.address} onChange={(e) => setFormData({...formData, address: e.target.value})} />
              <div className="modal-btns">
                <button type="submit" className="confirm-btn">Save</button>
                <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
