import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });

  const load = () =>
    api
      .get('/stores')
      .then((r) => setStores(r.data))
      .catch(() => {});

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      load();
    } catch (e) {}
  };

  return (
    <>
      <style>{`
        body {
          background: #fafbff !important;
          font-family: 'Inter', 'Segoe UI', Arial, sans-serif;
        }

        .dashboard-container {
          min-height: 100vh;
          width: 100%;
          padding: 0 0 54px 0;
          background: #fafbff;
          max-width: 900px;
          margin: 0 auto;
        }

        h1.dashboard-title {
          margin-top: 38px;
          margin-bottom: 18px;
          color: #211c39;
          font-size: 2.2rem;
          font-weight: 900;
          text-align: center;
          letter-spacing: 1px;
        }

        .card {
          margin: 24px auto 32px auto;
          padding: 36px 38px 32px 38px;
          background: #fff;
          border-radius: 18px;
          box-shadow: 0 8px 32px 0 rgba(76, 84, 210, 0.07);
          max-width: 480px;
          width: 100%;
          display: flex;
          flex-direction: column;
          align-items: center;
          animation: slideInFromLeft 1s cubic-bezier(0.4, 0, 0.2, 1) forwards;
        }

        @keyframes slideInFromLeft {
          0% {
            transform: translateX(-70px);
            opacity: 0;
          }
          100% {
            transform: translateX(0);
            opacity: 1;
          }
        }

        .card h3 {
          font-size: 1.35rem;
          font-weight: 700;
          color: #24293a;
          margin-bottom: 20px;
          margin-top: 0;
        }

        .card input {
          width: 390px;
          max-width: 100%;
          padding: 13px 15px;
          margin-bottom: 18px;
          border: 1px solid #e7e9ef;
          border-radius: 8px;
          background: #f5f7fa;
          font-size: 1rem;
          color: #2c2e3a;
          transition: border-color 0.2s;
          outline: none;
        }
        .card input:focus {
          border-color: #7f56da;
          background: #fff;
        }
        .card button {
          width: 100%;
          padding: 13px 0;
          background: #7f56da;
          color: #fff;
          font-size: 1.1rem;
          font-weight: 700;
          border: none;
          border-radius: 9px;
          margin-top: 6px;
          margin-bottom: 4px;
          cursor: pointer;
          box-shadow: 0 4px 18px rgba(127, 86, 218, 0.09);
          transition: background 0.18s, box-shadow 0.18s, transform 0.2s;
        }

        .card button:hover, .card button:focus {
          background: #6842c2;
          box-shadow: 0 6px 22px rgba(127, 86, 218, 0.15);
          transform: translateY(-2px) scale(1.03);
        }

        h2.stores-title {
          font-size: 1.28rem;
          font-weight: 700;
          color: #2d2f4a;
          margin-bottom: 10px;
          margin-top: 0;
          max-width: 600px;
          margin-left: auto;
          margin-right: auto;
        }

        ul.stores-list {
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 4px 16px rgba(127, 110, 218, 0.06);
          padding: 26px 38px;
          list-style: none;
          margin: 0 auto 0 auto;
          max-width: 600px;
          font-size: 1.09rem;
          color: #494553;
          font-weight: 500;
        }

        ul.stores-list li {
          padding: 8px 0;
          border-bottom: 1px solid #f2f2fa;
        }
        ul.stores-list li:last-child {
          border-bottom: none;
        }

        @media (max-width: 650px) {
          .card, ul.stores-list {
            padding: 20px 8px;
            max-width: 98%;
          }
          .card input {
            width: 100%;
          }
        }
      `}</style>
      <div className="dashboard-container">
        <h1 className="dashboard-title">Store Owner Dashboard</h1>
        <form onSubmit={submit} className="card">
          <h3>Create Store</h3>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />
          <input
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />
          <input
            placeholder="Address"
            value={form.address}
            onChange={(e) => setForm({ ...form, address: e.target.value })}
          />
          <button type="submit">Create</button>
        </form>
        <h2 className="stores-title">Your Stores</h2>
        <ul className="stores-list">
          {stores
            .filter((s) => s.owner_id)
            .map((s) => (
              <li key={s.store_id}>
                {s.name} <span style={{ color: '#7f56da' }}>({s.email})</span>
              </li>
            ))}
        </ul>
      </div>
    </>
  );
};

export default OwnerDashboard;
