import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './StoreOwner.css';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ 
    name: '', 
    email: '', 
    address: '' 
  });
  const [activeTab, setActiveTab] = useState('create');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState('');
  const navigate = useNavigate();

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const userId = user.user_id;
  const userEmail = user.email || '';

  console.log('ðŸ” Current User:', user);
  console.log('ðŸ” User ID:', userId);
  console.log('ðŸ” User Email:', userEmail);
  console.log('ðŸ” User Role:', user.role);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // Load stores function
  const load = async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      const allStores = response.data || [];
      setStores(allStores);
      
      // Enhanced Debug
      console.log('ðŸ“Š All stores loaded:', allStores.length);
      if (allStores.length > 0) {
        console.log('ðŸ” Full API Response Keys:', Object.keys(allStores[0]));
      }
      
      allStores.forEach((store, index) => {
        console.log(`Store ${index + 1}: "${store.name}" | Owner ID: ${store.owner_id} | Store Email: ${store.email}`);
      });
      
    } catch (e) {
      console.error("Failed to load stores", e);
      setAlert('Failed to load stores');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  // Submit store creation
  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    // Validation checks
    if (!form.name.trim()) {
      setAlert('Store name is required');
      setTimeout(() => setAlert(''), 3000);
      setLoading(false);
      return;
    }
    
    if (!form.email.trim()) {
      setAlert('Store email is required');
      setTimeout(() => setAlert(''), 3000);
      setLoading(false);
      return;
    }
    
    if (form.email === userEmail) {
      setAlert('Store email cannot be the same as your user email. Please use a different email address.');
      setTimeout(() => setAlert(''), 3000);
      setLoading(false);
      return;
    }
    
    // Email format validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) {
      setAlert('Please enter a valid email address');
      setTimeout(() => setAlert(''), 3000);
      setLoading(false);
      return;
    }
    
    try {
      console.log('ðŸ“¤ Creating store with data:', form);
      
      const response = await api.post('/stores', form);
      console.log('âœ… Store created successfully:', response.data);
      
      // Clear form
      setForm({ name: '', email: '', address: '' });
      
      // Reload stores and switch to stores tab
      await load();
      setActiveTab('stores');
      setCurrentPage(1);
      
      setAlert('Store created successfully!');
      setTimeout(() => setAlert(''), 3000);
      
    } catch (error) {
      console.error('âŒ Store creation error:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      
      // Handle specific error types
      if (error.response?.status === 409 || 
          error.response?.data?.error?.includes('duplicate key') ||
          error.response?.data?.message?.includes('already registered')) {
        setAlert('A store with this email already exists. Please use a different email address.');
      } else if (error.response?.status === 400) {
        setAlert(error.response.data.message || 'Invalid store data');
      } else if (error.response?.status === 401) {
        setAlert('Session expired. Please login again.');
        setTimeout(() => {
          localStorage.clear();
          navigate('/login');
        }, 2000);
      } else {
        setAlert('Failed to create store: ' + (error.response?.data?.message || error.message));
      }
      
      setTimeout(() => setAlert(''), 4000);
    } finally {
      setLoading(false);
    }
  };

  // Filter stores owned by the current user
  const ownerStores = stores.filter((store) => {
    if (!store.owner_id) return false;
    
    const storeOwnerId = Number(store.owner_id);
    const currentUserId = Number(userId);
    
    console.log(`Comparing: Store "${store.name}" | Owner ID: ${storeOwnerId} | Your ID: ${currentUserId}`);
    
    return storeOwnerId === currentUserId;
  });
  
  console.log('âœ… Your stores count:', ownerStores.length);
  console.log('ðŸ“‹ Your stores:', ownerStores);
  
  const totalPages = Math.ceil(ownerStores.length / itemsPerPage);
  
  const paginatedStores = ownerStores.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Generate a unique suggestion email
  const generateStoreEmail = () => {
    const timestamp = Date.now();
    const sanitizedName = form.name.toLowerCase().replace(/[^a-z0-9]/g, '');
    const suggestedEmail = `${sanitizedName || 'store'}.${timestamp}@storerate.com`;
    setForm({ ...form, email: suggestedEmail });
  };

  return (
    <div className="owner-dash-wrapper">
      {/* Background Effects */}
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

      {/* Sidebar */}
      <nav className="owner-sidebar">
        <div className="owner-logo">StoreRate<span>.</span></div>
        
        {/* Logout button */}
        <button onClick={handleLogout} className="owner-logout-btn">
          Logout
        </button>
        
        <div className="nav-group">
          <button
            className={activeTab === 'create' ? 'active' : ''}
            onClick={() => setActiveTab('create')}
          >
            Create Store
          </button>
          <button
            className={activeTab === 'stores' ? 'active' : ''}
            onClick={() => setActiveTab('stores')}
          >
            Your Stores
          </button>
        </div>

        {/* User Info */}
        <div className="sidebar-user-info">
          <div className="user-avatar">
            {user.name?.charAt(0)?.toUpperCase() || 'U'}
          </div>
          <div className="user-details">
            <strong>{user.name || 'Store Owner'}</strong>
            <span>Store Owner</span>
            <small>ID: {userId}</small>
            <small>Email: {userEmail}</small>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="owner-content">
        {activeTab === 'create' && (
          <div className="owner-glass-card fade-in">
            <h2>Register New Store</h2>
            <p className="card-subtitle">Enter details to add your store to the platform</p>
            
            {/* User Info Display */}
            <div className="user-info-badge">
              Store will be registered under: <strong>{user.name}</strong> (ID: {userId})
            </div>
            
            <form onSubmit={submit} className="owner-form">
              <div className="form-group">
                <label htmlFor="storeName">Store Name *</label>
                <input
                  id="storeName"
                  type="text"
                  placeholder="Enter store name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="storeEmail">
                  Store Contact Email * 
                  <button 
                    type="button" 
                    className="generate-email-btn"
                    onClick={generateStoreEmail}
                    disabled={!form.name.trim()}
                    title="Generate unique email based on store name"
                  >
                    Generate Unique Email
                  </button>
                </label>
                <input
                  id="storeEmail"
                  type="email"
                  placeholder="store.unique@example.com"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                />
                <div className="email-note">
                  <small>
                    <strong>Important:</strong> Must be different from your user email: 
                    <code>{userEmail}</code>
                  </small>
                  <small>Each store must have a unique email address.</small>
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="storeAddress">Store Address</label>
                <textarea
                  id="storeAddress"
                  placeholder="Enter store address (optional)"
                  value={form.address}
                  onChange={(e) => setForm({ ...form, address: e.target.value })}
                  rows="3"
                  className="owner-form-input"
                  maxLength="400"
                />
                <small className="char-count">
                  {form.address.length}/400 characters
                </small>
              </div>
              
              <button type="submit" className="owner-btn" disabled={loading}>
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    Creating...
                  </>
                ) : 'Create Store'}
              </button>
              
              <div className="form-tips">
                <h4>Tips for creating a store:</h4>
                <ul>
                  <li>Use a unique email address for each store</li>
                  <li>Store email will be used for contact purposes</li>
                  <li>Address helps customers find your store</li>
                  <li>After creation, you can view your store in "Your Stores" tab</li>
                </ul>
              </div>
            </form>
          </div>
        )}

        {activeTab === 'stores' && (
          <div className="owner-glass-card fade-in">
            <div className="content-header">
              <div>
                <h2>Your Stores Portfolio</h2>
                <p className="card-subtitle">
                  Showing stores for: <strong>{user.name}</strong>
                  <span className="store-count"> ({ownerStores.length} stores)</span>
                </p>
              </div>
              <div className="header-actions">
                <div className="user-id-badge">
                  Your User ID: <strong>{userId}</strong>
                </div>
                <button 
                  className="refresh-btn"
                  onClick={load}
                  disabled={loading}
                  title="Refresh store list"
                >
                  â†» Refresh
                </button>
              </div>
            </div>
            
            <div className="table-container">
              <table className="stores-table">
                <thead>
                  <tr>
                    <th>Store Name</th>
                    <th>Email</th>
                    <th>Address</th>
                    <th>Owner ID</th>
                    <th>Rating</th>
                    <th>Total Ratings</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {paginatedStores.map((store) => (
                    <tr key={store.store_id}>
                      <td className="font-bold">{store.name}</td>
                      <td>
                        <div className="store-email">
                          {store.email}
                          {store.email === userEmail && (
                            <span className="email-warning" title="Warning: Same as your user email">
                              âš 
                            </span>
                          )}
                        </div>
                      </td>
                      <td className="truncate-cell" title={store.address}>
                        {store.address || 'â€”'}
                      </td>
                      <td className="text-center">
                        <span className={`owner-id ${store.owner_id == userId ? 'match' : 'mismatch'}`}>
                          {store.owner_id}
                          {store.owner_id == userId && ' âœ“'}
                        </span>
                      </td>
                      <td className="text-center">
                        <span className="rating-pill">
                          â˜… {Number(store.average_rating || 0).toFixed(1)}
                        </span>
                      </td>
                      <td className="text-center">
                        <span className="rating-count">
                          {store.rating_count || 0}
                        </span>
                      </td>
                      <td>
                        <span className="status-badge">
                          {store.owner_id == userId ? 'Active' : 'Inactive'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              
              {ownerStores.length === 0 && (
                <div className="empty-state-card">
                  <div className="empty-icon">ðŸª</div>
                  <h3>No Stores Found</h3>
                  <p>
                    You don't have any stores yet. <br />
                    Your User ID is <strong>{userId}</strong>, but no stores have this owner ID.
                  </p>
                  
                  {/* Debug Info */}
                  <div className="debug-info">
                    <h4>Database Information:</h4>
                    <p>Total stores in database: {stores.length}</p>
                    <p>Your user email: <code>{userEmail}</code></p>
                    <p>Existing stores in database:</p>
                    <div className="existing-stores-list">
                      {stores.slice(0, 5).map(s => (
                        <div key={s.store_id} className="store-item">
                          <strong>"{s.name}"</strong> 
                          <span>Owner ID: {s.owner_id || 'NULL'}</span>
                          <span>Email: {s.email}</span>
                        </div>
                      ))}
                    </div>
                    <button 
                      className="debug-btn"
                      onClick={() => load()}
                    >
                      â†» Refresh Store List
                    </button>
                  </div>
                  
                  <button 
                    className="empty-action-btn"
                    onClick={() => setActiveTab('create')}
                  >
                    + Create Your First Store
                  </button>
                </div>
              )}
            </div>

            {/* Pagination */}
            {ownerStores.length > 0 && (
              <div className="owner-pagination">
                <button 
                  onClick={() => goToPage(currentPage - 1)} 
                  disabled={currentPage === 1}
                >
                  â† Previous
                </button>
                
                <div className="page-numbers">
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      onClick={() => goToPage(page)}
                      className={currentPage === page ? 'current' : ''}
                    >
                      {page}
                    </button>
                  ))}
                </div>
                
                <span className="page-indicator">
                  Page {currentPage} of {totalPages}
                </span>
                
                <button 
                  onClick={() => goToPage(currentPage + 1)} 
                  disabled={currentPage === totalPages}
                >
                  Next â†’
                </button>
              </div>
            )}
          </div>
        )}
      </main>

      {alert && (
        <div className={`owner-alert-toast ${alert.includes('successfully') ? 'success' : 'error'}`}>
          {alert}
        </div>
      )}
    </div>
  );
};

export default OwnerDashboard;
