import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create'); // 'create' or 'stores'

  const load = () =>
    api
      .get('/stores')
      .then((r) => setStores(r.data))
      .catch(() => {});

  useEffect(() => {
    load();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      load();
      setActiveTab('stores'); // Switch tab after creation
    } catch (e) {}
  };

  return (
    <>
      <style>{`
        body, html, #root {
          margin: 0;
          height: 100%;
          font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI',
            Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          background-color: #f4f7fc;
          color: #222;
        }
        .dashboard-wrapper {
          display: flex;
          height: 100vh;
          overflow: hidden;
          background: #fff;
        }
        nav.sidebar {
          width: 260px;
          min-width: 220px;
          background: linear-gradient(135deg, #7f56da, #7563fb 85%);
          color: white;
          display: flex;
          flex-direction: column;
          padding: 60px 0;
          font-weight: 700;
          font-size: 1.15rem;
          user-select: none;
          position: fixed;
          top: 0;
          left: 0;
          bottom: 0;
          z-index: 1000;
          box-shadow: 2px 0 16px 0 rgba(127,86,218,0.18);
        }
        nav.sidebar button {
          background: none;
          border: none;
          color: inherit;
          padding: 20px 32px;
          width: 100%;
          text-align: left;
          cursor: pointer;
          transition: background 0.3s, color 0.3s;
          font-weight: 600;
          font-size: 1.15rem;
          border-left: 5px solid transparent;
        }
        nav.sidebar button.active, nav.sidebar button:hover {
          background: rgba(255,255,255,0.1);
          color: #fff;
          border-left: 5px solid #fff;
        }
        main.content {
          margin-left: 260px;
          flex-grow: 1;
          height: 100vh;
          padding: 0;
          display: flex;
          align-items: center;
          justify-content: center;
          background: #fafbff;
          box-sizing: border-box;
        }
        .center-wrapper {
          width: 100%;
          max-width: 520px;
          padding: 48px 36px;
          background: #fff;
          border-radius: 18px;
          box-shadow: 0 12px 40px rgba(127, 86, 218, 0.12);
          display: flex;
          flex-direction: column;
          animation: fadeSlideIn 0.8s cubic-bezier(0.4,0,0.2,1);
        }
        form.create-store-form {
          width: 100%;
          display: flex;
          flex-direction: column;
        }
        form.create-store-form h2 {
          font-weight: 900;
          font-size: 2.1rem;
          color: #222a35;
          text-align: center;
          margin-bottom: 32px;
          margin-top: 0;
          user-select: none;
          letter-spacing: 1px;
        }
        form.create-store-form input {
          padding: 18px 20px;
          margin-bottom: 26px;
          font-size: 1.1rem;
          border-radius: 12px;
          border: 1.8px solid #ddd;
          background-color: #f8faff;
          transition: border-color 0.3s;
          outline: none;
        }
        form.create-store-form input:focus {
          border-color: #7f56da;
          background-color: #fff;
          box-shadow: 0 0 10px #b99aff88;
        }
        form.create-store-form button {
          background: linear-gradient(90deg, #7f56da 30%, #7563fb 110%);
          color: #fff;
          font-weight: 800;
          padding: 19px 0;
          font-size: 1.3rem;
          border-radius: 15px;
          border: none;
          box-shadow: 0 8px 30px rgba(127, 86, 218, 0.3);
          cursor: pointer;
          letter-spacing: 1px;
          transition: background 0.22s, box-shadow 0.22s, transform 0.19s;
        }
        form.create-store-form button:hover, form.create-store-form button:focus {
          background: linear-gradient(90deg, #6842c2 0%, #6842c2 90%);
          transform: translateY(-2px) scale(1.02);
          box-shadow: 0 14px 48px rgba(104, 66, 194, 0.44);
        }
        @keyframes fadeSlideIn {
          0% { opacity: 0; transform: translateY(18px); }
          100% { opacity: 1; transform: translateY(0); }
        }
        .stores-list-section {
          width: 100%;
          max-width: 700px;
        }
        .stores-list-section h2 {
          font-weight: 900;
          font-size: 2rem;
          color: #222a35;
          text-align: left;
          margin-bottom: 30px;
          margin-top: 0;
          user-select: none;
          letter-spacing: 1px;
        }
        ul.stores-list {
          list-style: none;
          padding: 0;
          margin: 0;
          max-height: 520px;
          overflow-y: auto;
          display: grid;
          grid-template-columns: 1fr;
          gap: 18px;
        }
        ul.stores-list li {
          background: #f4f7fc;
          border-radius: 11px;
          box-shadow: 0 2px 9px #7f56da19;
          padding: 18px 24px;
          font-weight: 600;
          font-size: 1.12rem;
          color: #7f56da;
          display: flex;
          justify-content: space-between;
          align-items: center;
          cursor: default;
          transition: box-shadow 0.2s;
          user-select: none;
        }
        ul.stores-list li:hover {
          box-shadow: 0 10px 28px #7f56da55;
        }
        ul.stores-list li span {
          font-weight: 400;
          font-size: 0.98rem;
          color: #7f56da;
          user-select: text;
        }
        @media (max-width: 800px) {
          main.content {
            margin-left: 0;
            padding: 24px 20px 40px;
          }
          nav.sidebar {
            position: relative;
            width: 100%;
            flex-direction: row;
            height: auto;
            min-width: 0;
            justify-content: center;
            align-items: center;
            box-shadow: none;
            padding: 12px 0;
          }
          nav.sidebar button {
            padding: 14px 8px;
            font-size: 1.05rem;
            flex-grow: 1;
            border-left: none;
            border-bottom: 3px solid transparent;
            text-align: center;
          }
          nav.sidebar button.active, nav.sidebar button:hover {
            border-left: none;
            border-bottom: 3px solid #fff;
          }
          .dashboard-wrapper {
            flex-direction: column;
            height: 100vh;
          }
        }
      `}</style>

      <div className="dashboard-wrapper" role="main" aria-label="Store owner dashboard">
        <nav className="sidebar" aria-label="Dashboard Navigation">
          <button
            type="button"
            className={activeTab === 'create' ? 'active' : ''}
            onClick={() => setActiveTab('create')}
            aria-current={activeTab === 'create' ? 'page' : undefined}
          >
            Create Store
          </button>
          <button
            type="button"
            className={activeTab === 'stores' ? 'active' : ''}
            onClick={() => setActiveTab('stores')}
            aria-current={activeTab === 'stores' ? 'page' : undefined}
          >
            Your Stores
          </button>
        </nav>

        <main className="content">
          {activeTab === 'create' && (
            <div className="center-wrapper">
              <form
                onSubmit={submit}
                className="create-store-form"
                aria-label="Create a new store form"
              >
                <h2>Create Store</h2>
                <input
                  type="text"
                  placeholder="Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                  aria-required="true"
                />
                <input
                  type="email"
                  placeholder="Email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                  aria-required="true"
                />
                <input
                  type="text"
                  placeholder="Address"
                  value={form.address}
                  onChange={(e) => setForm({ ...form, address: e.target.value })}
                />
                <button type="submit">Create</button>
              </form>
            </div>
          )}

          {activeTab === 'stores' && (
            <div className="center-wrapper">
              <section className="stores-list-section" aria-label="List of your stores">
                <h2>Your Stores</h2>
                <ul className="stores-list" tabIndex={0}>
                  {stores
                    .filter((s) => s.owner_id)
                    .map((s) => (
                      <li key={s.store_id}>
                        {s.name} <span>({s.email})</span>
                      </li>
                    ))}
                  {stores.filter((s) => s.owner_id).length === 0 && (
                    <p>You have no stores yet.</p>
                  )}
                </ul>
              </section>
            </div>
          )}
        </main>
      </div>
    </>
  );
};

export default OwnerDashboard;
