import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import "./AdminDashboard.css"; // External professional CSS

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [currentPage, setCurrentPage] = useState(1);
  const [storeSearch, setStoreSearch] = useState("");

  const itemsPerPage = 5;

  useEffect(() => {
    // Load Users and Stores
    api.get("/users").then((r) => setUsers(r.data)).catch(() => {});
    api.get("/stores").then((r) => setStores(r.data)).catch(() => {});
    
    // Load Admin Stats (Requirement: 19, 20, 21)
    api.get("/users/stats").then((r) => setStats(r.data)).catch(() => {});
  }, []);

  const filteredStores = useMemo(() => {
    return stores.filter((s) =>
      s.name.toLowerCase().includes(storeSearch.trim().toLowerCase())
    );
  }, [stores, storeSearch]);

  const data = activeTab === "users" ? users : filteredStores;
  const totalPages = Math.ceil(data.length / itemsPerPage);
  const paginatedData = data.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const goToPage = (page) => { if (page >= 1 && page <= totalPages) setCurrentPage(page); };

  useEffect(() => { setCurrentPage(1); }, [activeTab, storeSearch]);

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => setActiveTab("users")}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => setActiveTab("stores")}>Stores</button>
        </div>
      </nav>

      <main className="admin-main">
        {/* Requirement: Total users, Total stores, Total ratings */}
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            {activeTab === "stores" && (
              <input 
                className="admin-search" 
                placeholder="Filter by name..." 
                value={storeSearch} 
                onChange={(e) => setStoreSearch(e.target.value)} 
              />
            )}
          </div>

          <div className="admin-table-wrapper">
            <ul className="admin-list">
              {paginatedData.map((item) => (
                <li key={activeTab === "users" ? item.user_id : item.store_id} className="admin-list-item">
                  <div className="item-main-info">
                    <span className="item-name">{item.name}</span>
                    <span className="item-sub">
                      {activeTab === "users" ? `${item.email} • ${item.role}` : `${item.address || 'No address'}`}
                    </span>
                  </div>
                  {activeTab === "stores" && (
                    <div className="item-rating-info">
                      ★ {Number(item.average_rating).toFixed(1)} <span>({item.rating_count})</span>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>

          {totalPages > 1 && (
            <div className="admin-pagination">
              <button onClick={() => goToPage(currentPage - 1)} disabled={currentPage === 1}>Prev</button>
              <span className="page-indicator">Page {currentPage} of {totalPages}</span>
              <button onClick={() => goToPage(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default AdminDashboard;
