import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <>
      {/* Google Fonts */}
      <link
        href="https://fonts.googleapis.com/css2?family=Inter:wght@400;600;900&display=swap"
        rel="stylesheet"
      />
      <style>{`
        * {
          box-sizing: border-box;
        }
        body, html, #root {
          margin: 0;
          padding: 0;
          height: 100%;
          background-color: #fff !important;
          font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen,
            Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          overflow-x: hidden;
          color: #1f2937;
        }

         @keyframes slideInFromLeft {
    0% {
      transform: translateX(-100%);
      opacity: 0;
    }
    100% {
      transform: translateX(0);
      opacity: 1;
    }
  }

  .container {
    /* smoother slide-in */
    animation: slideInFromLeft 1.8s ease-out forwards;
  }
        .container {
          display: flex;
          justify-content: space-between;
          align-items: center;
          min-height: 100vh;
          padding: 3rem 6%;
          gap: 3rem;
          animation: slideInFromLeft 1s ease forwards;
        }

        .content {
          flex: 1 1 50%;
          max-width: 600px;
        }

        h1 {
          font-weight: 900;
          font-size: 3.2rem;
          margin-bottom: 1rem;
          letter-spacing: 0.05em;
          text-transform: uppercase;
          user-select: none;
          text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.3);
        }

        p.description {
          font-size: 1.25rem;
          font-weight: 500;
          line-height: 1.6;
          margin-bottom: 2.5rem;
          user-select: none;
          text-shadow: 1.5px 1.5px 7px rgba(0, 0, 0, 0.25);
        }

        .button-group {
          display: flex;
          gap: 1.8rem;
          flex-wrap: nowrap;
        }
        .btn {
          display: inline-flex;
          align-items: center;
          justify-content: center;
          font-weight: 700;
          border-radius: 14px;
          padding: 1rem 2.8rem;
          font-size: 1.2rem;
          text-decoration: none;
          cursor: pointer;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          user-select: none;
          border: none;
          min-width: 140px;
          box-shadow: 0 10px 30px rgba(65, 57, 191, 0.15);
        }

        .btn-login {
          background: linear-gradient(45deg, #7f56da, #7563fb);
          color: white;
          box-shadow: 0 12px 40px rgba(87, 80, 222, 0.35);
          border: none;
        }
        .btn-login:hover {
          background: linear-gradient(45deg, #7563fb, #5750de);
          box-shadow: 0 14px 48px rgba(117, 99, 251, 0.45);
          transform: translateY(-4px);
        }

        .btn-signup {
          background: transparent;
          color: #7f56da;
          border: 2.5px solid #7f56da;
          box-shadow: none;
        }
        .btn-signup:hover {
    
          color: #2c387e;
          box-shadow: 0 14px 48px rgba(170, 187, 255, 0.6);
          transform: translateY(-4px);
        }

        .image-container {
          flex: 1 1 50%;
          max-width: 600px;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        img.hero-image {
          width: 100%;
          height: auto;
        }

        footer {
          width: 100%;
          text-align: center;
          margin-top: 3rem;
          color: rgba(0, 0, 0, 0.7);
          font-weight: 500;
          letter-spacing: 0.06em;
          user-select: none;
          font-size: 1rem;
          text-shadow: none;
          position: absolute;
          bottom: 1.5rem;
          left: 0;
        }

        @media (max-width: 900px) {
          .container {
            flex-direction: column-reverse;
            padding: 2rem 5%;
            gap: 2rem;
          }
          .image-container,
          .content {
            max-width: 100%;
            flex: none;
            width: 100%;
          }
          h1 {
            font-size: 2.6rem;
          }
          p.description {
            font-size: 1.1rem;
          }
          footer {
            position: static;
            margin-top: 1.5rem;
          }
        }
      `}</style>

      <main className="container" role="main" aria-label="Home page section">
        <section className="content" aria-labelledby="homepage-welcome">
          <h1 id="homepage-welcome">Store Rating App</h1>
          <p className="description">
            Welcome! Discover, rate, and manage stores with rich feedback and seamless user experience.
          </p>
          <nav className="button-group" aria-label="Authentication actions">
            <Link to="/login" className="btn btn-login" aria-label="Login to your account">
              Login
            </Link>
            <Link to="/signup" className="btn btn-signup" aria-label="Create a new account">
              Signup
            </Link>
          </nav>
        </section>

        <section className="image-container">
          <img
            className="hero-image"
            src="https://img.freepik.com/free-vector/user-rating-feedback-customer-reviews-cartoon-web-icon-e-commerce-online-shopping-internet-buying-trust-metrics-top-rated-product_335657-778.jpg"
            alt="Store Rating Illustration"
          />
        </section>

        <footer>&copy; {new Date().getFullYear()} Store Rating App â€” Crafted for your success</footer>
      </main>
    </>
  );
};

export default HomePage;
