import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './StoreOwner.css';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState('');
  const navigate = useNavigate();

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const userId = user.user_id;
  const userName = user.name || 'Store Owner';

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  // Load all stores from API
  const loadAllStores = async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      setStores(response.data || []);
      console.log('All stores loaded:', response.data);
    } catch (e) {
      console.error("Failed to load stores", e);
      setAlert('Failed to load stores');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Check if user is logged in
    if (!userId) {
      setAlert('Please login first');
      setTimeout(() => {
        navigate('/login');
      }, 2000);
      return;
    }
    
    loadAllStores();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    
    if (!userId) {
      setAlert('Please login first');
      setTimeout(() => {
        navigate('/login');
      }, 2000);
      return;
    }

    setLoading(true);
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      await loadAllStores();
      setActiveTab('stores');
      setCurrentPage(1);
      setAlert('Store created successfully!');
      setTimeout(() => setAlert(''), 3000);
    } catch (e) {
      setAlert('Failed to add store: ' + (e.response?.data?.message || e.message));
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  // FIXED: Filter stores by logged-in user's ID
  const ownerStores = stores.filter((store) => {
    console.log('Store:', store.name, 'Owner ID:', store.owner_id, 'User ID:', userId);
    return store.owner_id === userId;
  });
  
  console.log('Owner stores count:', ownerStores.length);
  console.log('All stores count:', stores.length);
  
  const totalPages = Math.ceil(ownerStores.length / itemsPerPage);
  
  const paginatedStores = ownerStores.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) setCurrentPage(page);
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // Calculate statistics
  const totalRatings = ownerStores.reduce((sum, store) => sum + (store.rating_count || 0), 0);
  const averageRating = ownerStores.length > 0 
    ? (ownerStores.reduce((sum, store) => sum + (store.average_rating || 0), 0) / ownerStores.length).toFixed(1)
    : 0;

  return (
    <div className="owner-dash-wrapper">
      {/* Background Effects */}
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

      {/* Sidebar */}
      <nav className="owner-sidebar">
        <div className="owner-logo">StoreRate<span>.</span></div>
        
        {/* Logout button at top */}
        <button onClick={handleLogout} className="owner-logout-btn">
          Logout
        </button>
        
        <div className="nav-group">
          <button
            className={activeTab === 'create' ? 'active' : ''}
            onClick={() => setActiveTab('create')}
          >
            Create Store
          </button>
          <button
            className={activeTab === 'stores' ? 'active' : ''}
            onClick={() => setActiveTab('stores')}
          >
            Your Stores
          </button>
        </div>

        {/* User Info in Sidebar */}
        <div className="sidebar-user-info">
          <div className="user-avatar">
            {userName.charAt(0).toUpperCase()}
          </div>
          <div className="user-details">
            <strong>{userName}</strong>
            <span>Store Owner</span>
            <small>ID: {userId}</small>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="owner-content">
        {activeTab === 'create' && (
          <div className="owner-glass-card fade-in">
            <h2>Register New Store</h2>
            <p className="card-subtitle">Add your store to the platform</p>
            
            {/* Store Creation Form */}
            <form onSubmit={submit} className="owner-form">
              <div className="form-group">
                <label>Store Name *</label>
                <input
                  type="text"
                  placeholder="e.g., Best Electronics Store"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                  minLength="3"
                />
              </div>
              
              <div className="form-group">
                <label>Contact Email *</label>
                <input
                  type="email"
                  placeholder="store@example.com"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Address</label>
                <textarea
                  placeholder="Full store address"
                  value={form.address}
                  onChange={(e) => setForm({ ...form, address: e.target.value })}
                  rows="3"
                  className="owner-form-input"
                  maxLength="400"
                />
                <small className="char-count">{form.address.length}/400 characters</small>
              </div>
              
              <div className="form-note">
                <small>Store will be registered under: <strong>{userName}</strong> (ID: {userId})</small>
              </div>
              
              <button type="submit" className="owner-btn" disabled={loading}>
                {loading ? 'Creating...' : 'Create Store'}
              </button>
            </form>
          </div>
        )}

        {activeTab === 'stores' && (
          <>
            {/* Stats Cards - Like Admin Dashboard */}
            <div className="owner-stats-grid">
              <div className="stat-card">
                <span className="stat-label">Total Stores</span>
                <span className="stat-value">{ownerStores.length}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Total Ratings</span>
                <span className="stat-value">{totalRatings}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Avg Rating</span>
                <span className="stat-value">{averageRating}</span>
              </div>
            </div>

            <div className="owner-glass-card fade-in">
              <div className="content-header">
                <h2>Your Stores Portfolio</h2>
                <p className="card-subtitle">
                  Managing {ownerStores.length} store{ownerStores.length !== 1 ? 's' : ''}
                </p>
              </div>
              
              {ownerStores.length > 0 ? (
                <>
                  <div className="table-container">
                    <table className="stores-table">
                      <thead>
                        <tr>
                          <th>Store Name</th>
                          <th>Contact Email</th>
                          <th>Address</th>
                          <th className="text-center">Rating</th>
                          <th className="text-center">Total Ratings</th>
                          <th>Status</th>
                          <th className="text-center">Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {paginatedStores.map((store) => (
                          <tr key={store.store_id}>
                            <td className="font-bold">{store.name}</td>
                            <td>{store.email}</td>
                            <td className="truncate-cell" title={store.address}>
                              {store.address || '‚Äî'}
                            </td>
                            <td className="text-center">
                              <span className="rating-pill">
                                ‚òÖ {Number(store.average_rating || 0).toFixed(1)}
                              </span>
                            </td>
                            <td className="text-center">
                              <span className="rating-count">
                                {store.rating_count || 0}
                              </span>
                            </td>
                            <td>
                              <span className="status-badge active">Active</span>
                            </td>
                            <td className="text-center">
                              <button 
                                className="icon-view-btn"
                                title="View Details"
                                onClick={() => alert(`View details for ${store.name}`)}
                              >
                                üëÅÔ∏è
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>

                  {/* Pagination */}
                  {ownerStores.length > itemsPerPage && (
                    <div className="owner-pagination">
                      <button 
                        onClick={() => goToPage(currentPage - 1)} 
                        disabled={currentPage === 1}
                      >
                        Prev
                      </button>
                      
                      <span className="page-indicator">
                        Page {currentPage} of {totalPages}
                      </span>
                      
                      <button 
                        onClick={() => goToPage(currentPage + 1)} 
                        disabled={currentPage === totalPages}
                      >
                        Next
                      </button>
                    </div>
                  )}
                </>
              ) : (
                <div className="empty-state-card">
                  <div className="empty-icon">üè™</div>
                  <h3>No Stores Yet</h3>
                  <p>You haven't created any stores yet. Create your first store to get started!</p>
                  <button 
                    className="empty-action-btn"
                    onClick={() => setActiveTab('create')}
                  >
                    Create Your First Store
                  </button>
                </div>
              )}
            </div>
          </>
        )}
      </main>

      {alert && <div className="owner-alert-toast">{alert}</div>}
    </div>
  );
};

export default OwnerDashboard;
