import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './StoreOwner.css';

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', address: '' });
  const [activeTab, setActiveTab] = useState('create');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState('');
  const navigate = useNavigate();

  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const userId = user.user_id;

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const load = async () => {
    setLoading(true);
    try {
      const response = await api.get('/stores');
      const allStores = response.data || [];
      setStores(allStores);
    } catch (e) {
      console.error("Failed to load stores", e);
      setAlert('Failed to load stores');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/stores', form);
      setForm({ name: '', email: '', address: '' });
      await load();
      setActiveTab('stores');
      setCurrentPage(1);
      setAlert('Store created successfully!');
      setTimeout(() => setAlert(''), 3000);
    } catch (e) {
      setAlert('Failed to add store');
      setTimeout(() => setAlert(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  const ownerStores = stores.filter((store) => Number(store.owner_id) === Number(userId));
  const totalPages = Math.ceil(ownerStores.length / itemsPerPage);
  const paginatedStores = ownerStores.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  const goToPage = (page) => { if (page >= 1 && page <= totalPages) setCurrentPage(page); };
  const handleLogout = () => { localStorage.clear(); navigate('/login'); };

  return (
    <div className="owner-dash-wrapper">
      <div className="dash-glow dash-glow-1" />
      <div className="dash-glow dash-glow-2" />

      <nav className="owner-sidebar">
        <div className="owner-sidebar-top">
          <div className="owner-logo">StoreRate<span>.</span></div>
          
          <div className="owner-nav-content">
            <div className="nav-group">
              <button className={activeTab === 'create' ? 'active' : ''} onClick={() => setActiveTab('create')}>
                <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M12 5v14M5 12h14"/></svg>
                Create Store
              </button>
              <button className={activeTab === 'stores' ? 'active' : ''} onClick={() => setActiveTab('stores')}>
                <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"/><line x1="3" y1="9" x2="21" y2="9"/><line x1="9" y1="21" x2="9" y2="9"/></svg>
                Your Stores
              </button>
            </div>

            <button onClick={handleLogout} className="owner-logout-btn">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/><polyline points="16 17 21 12 16 7"/><line x1="21" y1="12" x2="9" y2="12"/></svg>
              Logout
            </button>
          </div>
        </div>

        <div className="sidebar-user-info">
          <div className="user-avatar">{user.name?.charAt(0)?.toUpperCase() || 'U'}</div>
          <div className="user-details">
            <strong>{user.name || 'Store Owner'}</strong>
            <span>Store Owner</span>
            <small>ID: {userId}</small>
          </div>
        </div>
      </nav>

      <main className="owner-content">
        <div className="content-container">
          {activeTab === 'create' && (
            <div className="owner-glass-card fade-in">
              <div className="card-header-main">
                <h2>Register New Store</h2>
                <p className="card-subtitle">Expand your business presence on StoreRate</p>
              </div>
              
              <div className="user-info-badge">
                Registering as: <strong>{user.name}</strong>
              </div>
              
              <form onSubmit={submit} className="owner-form">
                <div className="form-group">
                  <label>Store Name</label>
                  <input type="text" placeholder="e.g. Blue Tokai Coffee" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
                </div>
                <div className="form-group">
                  <label>Contact Email</label>
                  <input type="email" placeholder="store@example.com" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} required />
                </div>
                <div className="form-group">
                  <label>Business Address</label>
                  <textarea placeholder="Full street address..." value={form.address} onChange={(e) => setForm({ ...form, address: e.target.value })} rows="3" />
                </div>
                <button type="submit" className="owner-btn" disabled={loading}>
                  {loading ? 'Processing...' : 'Add Store to Platform'}
                </button>
              </form>
            </div>
          )}

          {activeTab === 'stores' && (
            <div className="owner-glass-card fade-in full-width">
              <div className="content-header-row">
                <div>
                  <h2>Your Stores Portfolio</h2>
                  <p className="card-subtitle">Manage and monitor your registered locations</p>
                </div>
                <div className="store-stats-pill">
                  {ownerStores.length} Stores Total
                </div>
              </div>
              
              <div className="table-container">
                <table className="stores-table">
                  <thead>
                    <tr>
                      <th>Store Name</th>
                      <th>Email Address</th>
                      <th>Location</th>
                      <th className="text-center">Avg Rating</th>
                      <th className="text-center">Reviews</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {paginatedStores.map((store) => (
                      <tr key={store.store_id}>
                        <td className="font-bold">{store.name}</td>
                        <td className="text-muted">{store.email}</td>
                        <td className="truncate-cell">{store.address || '‚Äî'}</td>
                        <td className="text-center">
                          <span className="rating-pill">‚òÖ {Number(store.average_rating || 0).toFixed(1)}</span>
                        </td>
                        <td className="text-center">
                          <span className="rating-count-clean">{store.rating_count || 0}</span>
                        </td>
                        <td><span className="status-badge">Active</span></td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                
                {ownerStores.length === 0 && (
                  <div className="empty-state-card">
                    <div className="empty-icon">üè™</div>
                    <h3>No stores registered yet</h3>
                    <button className="empty-action-btn" onClick={() => setActiveTab('create')}>Get Started</button>
                  </div>
                )}
              </div>

              {ownerStores.length > itemsPerPage && (
                <div className="owner-pagination">
                  <button onClick={() => goToPage(currentPage - 1)} disabled={currentPage === 1}>Prev</button>
                  <span className="page-indicator">Page {currentPage} of {totalPages}</span>
                  <button onClick={() => goToPage(currentPage + 1)} disabled={currentPage === totalPages}>Next</button>
                </div>
              )}
            </div>
          )}
        </div>
      </main>

      {alert && <div className="owner-alert-toast">{alert}</div>}
    </div>
  );
};

export default OwnerDashboard;
