import React, { useEffect, useState, useMemo } from "react";
import api from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState({ totalUsers: 0, totalStores: 0, totalRatings: 0 });
  const [activeTab, setActiveTab] = useState("users");
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const itemsPerPage = 5;
  const [currentPage, setCurrentPage] = useState(1);

  const loadData = async () => {
    try {
      const [uRes, sRes, statsRes] = await Promise.all([
        api.get("/users"),
        api.get("/stores"),
        api.get("/users/stats")
      ]);
      setUsers(uRes.data);
      setStores(sRes.data);
      setStats(statsRes.data);
    } catch (err) { console.error("Data load failed"); }
  };

  useEffect(() => { loadData(); }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm("Permanently delete this user from the database?")) {
      try {
        await api.delete(`/users/${userId}`);
        loadData(); // Refresh list after deletion
      } catch (err) { alert("Delete failed"); }
    }
  };

  const filteredData = useMemo(() => {
    const data = activeTab === "users" ? users : stores;
    return data.filter(item => 
      item.name.toLowerCase().includes(search.trim().toLowerCase()) ||
      item.email.toLowerCase().includes(search.trim().toLowerCase()) ||
      (item.address && item.address.toLowerCase().includes(search.trim().toLowerCase()))
    );
  }, [users, stores, activeTab, search]);

  const paginatedData = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);

  return (
    <div className="admin-wrapper">
      <div className="dash-glow dash-glow-1" />
      
      <nav className="admin-sidebar">
        <div className="admin-logo">StoreRate<span>.</span></div>
        <div className="admin-nav-group">
          <button className={activeTab === "users" ? "active" : ""} onClick={() => setActiveTab("users")}>Users</button>
          <button className={activeTab === "stores" ? "active" : ""} onClick={() => setActiveTab("stores")}>Stores</button>
        </div>
        <button onClick={handleLogout} className="admin-logout-btn">Logout</button>
      </nav>

      <main className="admin-main">
        {/* Requirement: Dashboard displaying totals */}
        <section className="admin-stats-grid">
          <div className="stat-card">
            <span className="stat-label">Total Users</span>
            <span className="stat-value">{stats.totalUsers}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Stores</span>
            <span className="stat-value">{stats.totalStores}</span>
          </div>
          <div className="stat-card">
            <span className="stat-label">Total Ratings</span>
            <span className="stat-value">{stats.totalRatings}</span>
          </div>
        </section>

        <section className="admin-content-card">
          <div className="content-header">
            <h2>{activeTab === "users" ? "Platform Users" : "Registered Stores"}</h2>
            <input 
              className="admin-search" 
              placeholder="Search by name, email, address..." 
              value={search} 
              onChange={(e) => setSearch(e.target.value)} 
            />
          </div>

          <ul className="admin-list">
            {paginatedData.map((item) => (
              <li key={activeTab === "users" ? item.user_id : item.store_id} className="admin-list-item">
                <div className="item-main-info">
                  <span className="item-name">{item.name}</span>
                  <span className="item-sub">
                    {item.email} • {activeTab === "users" ? item.role : item.address}
                  </span>
                  {/* Requirement: If Store Owner, show Rating */}
                  {(activeTab === "stores" || item.role === "Store Owner") && (
                    <div className="item-rating-info">★ {Number(item.average_rating || item.rating || 0).toFixed(1)}</div>
                  )}
                </div>
                <div className="item-actions">
                  {activeTab === "users" && (
                    <button onClick={() => handleDeleteUser(item.user_id)} className="delete-btn">Delete</button>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </section>
      </main>
    </div>
  );
};

export default AdminDashboard;
