          margin-top: 22px;
          gap: 8px;
          flex-wrap: wrap;
          user-select: none;
        }
        .pagination button {
          background: #7f56da;
          border: none;
          border-radius: 8px;
          color: white;
          font-weight: 600;
          font-size: 1rem;
          padding: 6px 14px;
          cursor: pointer;
          transition: background-color 0.2s ease;
        }
        .pagination button[disabled] {
          background: #bda8f9;
          cursor: not-allowed;
        }
        .pagination button:hover:not([disabled]) {
          background: #6842c2;
        }
        @keyframes fadeSlideIn {
          0% { opacity: 0; transform: translateY(18px); }
          100% { opacity: 1; transform: translateY(0); }
        }
        @media (max-width: 800px) {
          .content-wrapper {
            flex-direction: column;
          }
          nav.sidebar {
            width: 100%;
            min-width: auto;
            height: 56px;
            padding: 0 12px;
            flex-direction: row;
            justify-content: space-around;
            box-shadow: 0 2px 5px #7f56da55;
            border-radius: 0;
            position: relative;
          }
          nav.sidebar button {
            padding: 12px 16px;
            font-size: 1rem;
            border-radius: 12px;
            border-left: none;
            margin-bottom: 0;
          }
          nav.sidebar button.active,
          nav.sidebar button:hover {
            border-left: none;
            background-color: #6842c2;
            color: white;
          }
          main.content {
            margin-left: 0;
            padding: 36px 16px 48px;
          }
          ul.stores-list {
            grid-template-columns: 1fr !important;
            max-height: 300px;
          }
        }
      `}</style>

      <div className="dashboard-wrapper" role="main" aria-label="Store owner dashboard">
        <div className="top-navbar" aria-label="Page title bar">
          Owner Dashboard
        </div>

        <div className="content-wrapper">
          <nav className="sidebar" aria-label="Dashboard Navigation">
            <button
              type="button"
              className={activeTab === 'create' ? 'active' : ''}
              onClick={() => setActiveTab('create')}
              aria-current={activeTab === 'create' ? 'page' : undefined}
            >
              Create Store
            </button>
            <button
              type="button"
              className={activeTab === 'stores' ? 'active' : ''}
              onClick={() => setActiveTab('stores')}
              aria-current={activeTab === 'stores' ? 'page' : undefined}
            >
              Your Stores
            </button>
          </nav>

          <main className="content">
            {activeTab === 'create' && (
              <div className="center-wrapper">
                <form
                  onSubmit={submit}
                  className="create-store-form"
                  aria-label="Create a new store form"
                >
                  <h2>Create Store</h2>
                  <input
                    type="text"
                    placeholder="Name"
                    value={form.name}
                    onChange={(e) => setForm({ ...form, name: e.target.value })}
                    required
                    aria-required="true"
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    value={form.email}
                    onChange={(e) => setForm({ ...form, email: e.target.value })}
                    required
                    aria-required="true"
                  />
                  <input
                    type="text"
                    placeholder="Address"
                    value={form.address}
                    onChange={(e) => setForm({ ...form, address: e.target.value })}
                  />
                  <button type="submit">Create</button>
                </form>
              </div>
            )}

            {activeTab === 'stores' && (
              <div className="center-wrapper">
                <section className="stores-list-section" aria-label="List of your stores">
                  <h2>Your Stores</h2>
                  <ul className="stores-list" tabIndex={0}>
                    {paginatedStores.map((s) => (
                      <li key={s.store_id}>
                        {s.name} <span>({s.email})</span>
                      </li>
                    ))}
                    {ownerStores.length === 0 && <p>You have no stores yet.</p>}
                  </ul>

                  {/* Pagination buttons */}
                  {ownerStores.length > itemsPerPage && (
                    <div className="pagination" role="navigation" aria-label="Pagination Navigation">
                      <button
                        onClick={() => goToPage(currentPage - 1)}
                        disabled={currentPage === 1}
                        aria-label="Previous page"
                      >
                        &larr; Prev
                      </button>
                      {[...Array(totalPages)].map((_, idx) => {
                        const page = idx + 1;
                        return (
                          <button
                            key={page}
                            onClick={() => goToPage(page)}
                            aria-current={page === currentPage ? 'page' : undefined}
                            style={{
                              fontWeight: page === currentPage ? 'bold' : 'normal',
                              backgroundColor: page === currentPage ? '#6842c2' : undefined,
                            }}
                          >
                            {page}
                          </button>
                        );
                      })}
                      <button
                        onClick={() => goToPage(currentPage + 1)}
                        disabled={currentPage === totalPages}
                        aria-label="Next page"
                      >
                        Next &rarr;
                      </button>
                    </div>
                  )}
                </section>
              </div>
            )}
          </main>
        </div>
      </div>
    </>
  );
};

export default OwnerDashboard;
